import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Paper, 
  Button, 
  Typography, 
  CircularProgress 
} from "@mui/material";
import "../App.css";

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    project_name: "",
    description: "",
    start_date: "",
    end_date: "",
    budget: "",
    client_id: clientId,
  });

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    const fetchClientProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/${clientId}`);
        const updatedProjects = response.data.map((project) => ({
          ...project,
          team_members: Array.isArray(project.team_members) ? project.team_members : [],
        }));
        setProjects(updatedProjects);
      } catch (err) {
        setError("Failed to load projects.");
      }
    };

    fetchClientDetails();
    fetchClientProjects();
  }, [clientId]);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5005/api/projects", formData);
      setProjects([...projects, response.data]);
      setShowForm(false);
      console.log(response.data);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  if (error) {
    return <div>{error}</div>;
  }

  if (!client) {
    return (
      <div style={{ textAlign: "center" }}>
        <CircularProgress />
        <Typography>Loading...</Typography>
      </div>
    );
  }

  return (
    <div>
      <Typography variant="h4" align="center" gutterBottom>
        Projects for {client.client_name}
      </Typography>

      {/* Display Projects in Material UI Table */}
      <Typography variant="h6" gutterBottom>
        Projects
      </Typography>
      {projects.length === 0 ? (
        <Typography>No projects found for this client.</Typography>
      ) : (
        <TableContainer component={Paper} elevation={3}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Project Name</TableCell>
                <TableCell>Description</TableCell>
                <TableCell>Start Date</TableCell>
                <TableCell>End Date</TableCell>
                <TableCell>Budget</TableCell>
                <TableCell>Team Members</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {projects.map((project) => (
                <TableRow key={project._id}>
                  <TableCell>{project.project_name}</TableCell>
                  <TableCell>{project.description}</TableCell>
                  <TableCell>{new Date(project.start_date).toLocaleDateString()}</TableCell>
                  <TableCell>{new Date(project.end_date).toLocaleDateString()}</TableCell>
                  <TableCell>{project.budget}</TableCell>
                  <TableCell>{(project.team_members || []).join(", ")}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}

      <div className="client-profile-card" style={{ marginTop: "20px" }}>
        {/* Button to Show/Hide Form */}
        <Button 
          variant="contained" 
          color={showForm ? "secondary" : "primary"} 
          onClick={() => setShowForm(!showForm)} 
          fullWidth
        >
          {showForm ? "Cancel" : "Add New Project"}
        </Button>

        {/* Project Creation Form */}
        {showForm && (
          <div className="project-form" style={{ marginTop: "20px" }}>
            <Typography variant="h6" gutterBottom>Create a New Project</Typography>
            <form onSubmit={handleFormSubmit}>
              <div>
                <label>Project Name</label>
                <input
                  type="text"
                  name="project_name"
                  value={formData.project_name}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <div>
                <label>Description</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleFormChange}
                  className="description-input"
                />
              </div>
              <div>
                <label>Start Date</label>
                <input
                  type="date"
                  name="start_date"
                  value={formData.start_date}
                  onChange={handleFormChange}
                  className="date-input"
                />
              </div>
              <div>
                <label>End Date</label>
                <input
                  type="date"
                  name="end_date"
                  value={formData.end_date}
                  onChange={handleFormChange}
                  className="date-input"
                />
              </div>
              <div>
                <label>Budget</label>
                <input
                  type="number"
                  name="budget"
                  value={formData.budget}
                  onChange={handleFormChange}
                />
              </div>
              <div>
                <Button type="submit" variant="contained" color="primary">
                  Create Project
                </Button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

