import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const ProjectsPage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch projects from the backend
    const fetchProjects = async () => {
      try {
        const response = await axios.get("http://localhost:5005/api/projects"); // Replace with your API endpoint
        setProjects(response.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch projects. Please try again later.");
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  const handleViewDetails = (projectId) => {
    navigate(`/project/${projectId}`);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={pageStyle}>
    <h1>All Projects</h1>
    {projects.length === 0 ? (
      <p>No projects found.</p>
    ) : (
      <table style={tableStyle}>
        <thead>
          <tr>
            <th>Project ID</th>
            <th>Project Name</th>
            <th>Client</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {projects.map((project) => (
            <tr key={project._id}> {/* Using _id for the unique key */}
              <td>{project._id}</td> {/* Using _id as Project ID */}
              <td>{project.project_name}</td>
              <td>{project.client_id.client_name}</td> {/* Accessing client_name from populated client_id */}
              <td>{project.status}</td>
              <td>
              <button onClick={() => handleViewDetails(project._id)} style={buttonStyle}>
                    View Details
                  </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    )}
  </div>


  );
};

const pageStyle = {
  padding: "20px",
  fontFamily: "Arial, sans-serif",
};

const tableStyle = {
  width: "100%",
  borderCollapse: "collapse",
  marginTop: "20px",
};

const buttonStyle = {
    padding: "10px 20px",
    margin: "10px 5px",
    border: "none",
    borderRadius: "4px",
    backgroundColor: "#739ea7",
    color: "white",
    cursor: "pointer",
    fontSize: "14px",
  };

export default ProjectsPage;
