import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Box, Typography, TextField, Select, MenuItem, 
  Table, TableBody, TableCell, TableContainer, TableHead, 
  TableRow, Paper, Button, Dialog, DialogActions, DialogContent, 
  DialogTitle, InputLabel, FormControl 
} from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import { Link } from 'react-router-dom';
// import Sidebar from '../components/Sidebar';
import PendingUsers from '../components/PendingUser';

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState('');
  const [filters, setFilters] = useState({ region: '', industry: '', status: '' });
  const [sortModel, setSortModel] = useState([{ field: 'client_name', sort: 'asc' }]);
  const [authToken] = useState(localStorage.getItem('token') || '');
  const [editingClient, setEditingClient] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchClients();
  }, [filters, sortModel]);

  const fetchClients = async () => {
    try {
      const query = new URLSearchParams({
        search,
        region: filters.region,
        industry: filters.industry,
        status: filters.status,
        sortColumn: sortModel[0]?.field,
        sortOrder: sortModel[0]?.sort
      });

      const response = await axios.get(`http://localhost:5005/api/users?${query.toString()}`, {
        headers: { Authorization: `Bearer ${authToken}` },
      });
      setClients(response.data);
    } catch (err) {
      console.error('Error fetching clients:', err);
    }
  };

  const handleSearch = (e) => setSearch(e.target.value);

  const handleFilterChange = (e) => setFilters({ ...filters, [e.target.name]: e.target.value });

  const handleEditClick = (client) => setEditingClient(client);

  const handleEditChange = (e) => setEditingClient({ ...editingClient, [e.target.name]: e.target.value });

  const handleEditSubmit = async () => {
    try {
      await axios.put(
        `http://localhost:5005/api/users/${editingClient._id}`,
        editingClient,
        { headers: { Authorization: `Bearer ${authToken}` } }
      );
      setClients(clients.map(client => (client._id === editingClient._id ? editingClient : client)));
      setEditingClient(null);
    } catch (err) {
      console.error('Error updating client:', err);
      setError('Failed to update client.');
    }
  };

  const handleDelete = async (clientId) => {
    try {
      await axios.delete(`http://localhost:5005/api/users/${clientId}`, {
        headers: { Authorization: `Bearer ${authToken}` },
      });
      setClients(clients.filter(client => client._id !== clientId));
      alert('Client deleted successfully');
    } catch (err) {
      console.error('Error deleting client:', err);
    }
  };

  return (
    <Box display="flex">
      <Sidebar />
      <Box sx={{ flexGrow: 1, p: 3 }}>
        <PendingUsers />
        <Typography variant="h4" sx={{ mb: 3 }}>Clients Dashboard</Typography>

        {/* Search & Filters */}
        <Box display="flex" gap={2} mb={3}>
          <TextField 
            label="Search Clients" 
            variant="outlined" 
            value={search} 
            onChange={handleSearch} 
            fullWidth 
          />
          <FormControl fullWidth>
            <InputLabel>Region</InputLabel>
            <Select name="region" value={filters.region} onChange={handleFilterChange}>
              <MenuItem value="">All Regions</MenuItem>
              <MenuItem value="North America">North America</MenuItem>
              <MenuItem value="Europe">Europe</MenuItem>
              <MenuItem value="Asia">Asia</MenuItem>
            </Select>
          </FormControl>
          <FormControl fullWidth>
            <InputLabel>Industry</InputLabel>
            <Select name="industry" value={filters.industry} onChange={handleFilterChange}>
              <MenuItem value="">All Industries</MenuItem>
              <MenuItem value="Finance">Finance</MenuItem>
              <MenuItem value="Technology">Technology</MenuItem>
              <MenuItem value="Healthcare">Healthcare</MenuItem>
            </Select>
          </FormControl>
          <FormControl fullWidth>
            <InputLabel>Status</InputLabel>
            <Select name="status" value={filters.status} onChange={handleFilterChange}>
              <MenuItem value="">All Statuses</MenuItem>
              <MenuItem value="Active">Active</MenuItem>
              <MenuItem value="Inactive">Inactive</MenuItem>
            </Select>
          </FormControl>
        </Box>

        {/* Clients Table */}
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Client Name</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Industry</TableCell>
                <TableCell>Email</TableCell>
                <TableCell>Phone</TableCell>
                <TableCell>Contact Person</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {clients.map(client => (
                <TableRow key={client._id}>
                  <TableCell>
                    <Link to={`/client/${client._id}`}>{client.client_name}</Link>
                  </TableCell>
                  <TableCell>{client.status}</TableCell>
                  <TableCell>{client.industry}</TableCell>
                  <TableCell>{client.email}</TableCell>
                  <TableCell>{client.phone}</TableCell>
                  <TableCell>{client.contact_person}</TableCell>
                  <TableCell>
                    <Button variant="contained" color="primary" onClick={() => handleEditClick(client)}>Edit</Button>
                    <Button variant="contained" color="error" onClick={() => handleDelete(client._id)}>Delete</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Edit Modal */}
        {editingClient && (
          <Dialog open={true} onClose={() => setEditingClient(null)}>
            <DialogTitle>Edit Client</DialogTitle>
            <DialogContent>
              <TextField label="Client Name" name="client_name" value={editingClient.client_name} onChange={handleEditChange} fullWidth />
              <TextField label="Contact Person" name="contact_person" value={editingClient.contact_person} onChange={handleEditChange} fullWidth />
            </DialogContent>
            <DialogActions>
              <Button onClick={() => setEditingClient(null)}>Cancel</Button>
              <Button onClick={handleEditSubmit} variant="contained">Save</Button>
            </DialogActions>
          </Dialog>
        )}
      </Box>
    </Box>
  );
};

export default Dashboard;
