import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState('');
  const [filters, setFilters] = useState({
    region: '',
    industry: '',
    status: '',
  });
  const [sortOrder, setSortOrder] = useState('asc'); // 'asc' or 'desc'
  const [sortColumn, setSortColumn] = useState('client_name'); // Default column to sort
  const [authToken, setAuthToken] = useState(localStorage.getItem('token') || ''); // Get the token from localStorage
  const [editingClientId, setEditingClientId] = useState(null); // Track client being edited
  const [editData, setEditData] = useState({}); // Track data being edited

  useEffect(() => {
    fetchClients();
  }, [filters, sortColumn, sortOrder]);

  const fetchClients = () => {
    const query = new URLSearchParams({
      search,
      region: filters.region,
      industry: filters.industry,
      status: filters.status,
      sortColumn,
      sortOrder,
    });

    axios
      .get(`http://localhost:5005/api/users?${query.toString()}`, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      })
      .then((response) => {
        setClients(response.data);
      })
      .catch((err) => {
        console.error('Error fetching clients:', err);
      });
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleSort = (column) => {
    const newSortOrder = sortOrder === 'asc' ? 'desc' : 'asc';
    setSortOrder(newSortOrder);
    setSortColumn(column);
  };

  const handleEdit = (clientId) => {
    setEditingClientId(clientId);
    const clientToEdit = clients.find((client) => client._id === clientId);
    setEditData(clientToEdit);
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditData({ ...editData, [name]: value });
  };

  const handleSaveEdit = () => {
    axios
      .put(`http://localhost:5005/api/users/${editingClientId}`, editData, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      })
      .then((response) => {
        setClients((prevClients) =>
          prevClients.map((client) =>
            client._id === editingClientId ? response.data : client
          )
        );
        setEditingClientId(null);
      })
      .catch((err) => {
        console.error('Error saving client edits:', err);
      });
  };

  const handleDelete = (clientId) => {
    if (window.confirm('Are you sure you want to delete this client?')) {
      axios
        .delete(`http://localhost:5005/api/users/${clientId}`, {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        })
        .then(() => {
          setClients((prevClients) => prevClients.filter((client) => client._id !== clientId));
        })
        .catch((err) => {
          console.error('Error deleting client:', err);
        });
    }
  };

  return (
    <div className="dashboard">
      <h1>Client Dashboard</h1>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search clients..."
        value={search}
        onChange={handleSearch}
      />

      {/* Filter Options */}
      <select name="region" onChange={handleFilterChange} value={filters.region}>
        <option value="">All Regions</option>
        <option value="North America">North America</option>
        <option value="Europe">Europe</option>
        <option value="Asia">Asia</option>
      </select>

      <select name="industry" onChange={handleFilterChange} value={filters.industry}>
        <option value="">All Industries</option>
        <option value="Finance">Finance</option>
        <option value="Technology">Technology</option>
        <option value="Healthcare">Healthcare</option>
      </select>

      <select name="status" onChange={handleFilterChange} value={filters.status}>
        <option value="">All Statuses</option>
        <option value="Active">Active</option>
        <option value="Inactive">Inactive</option>
      </select>

      {/* Client Data Table */}
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort('client_name')}>Client Name</th>
            <th onClick={() => handleSort('industry')}>Industry</th>
            <th onClick={() => handleSort('status')}>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {clients.length > 0 ? (
            clients.map((client) => (
              <tr key={client._id}>
                {editingClientId === client._id ? (
                  <>
                    <td>
                      <input
                        type="text"
                        name="client_name"
                        value={editData.client_name}
                        onChange={handleEditChange}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        name="industry"
                        value={editData.industry}
                        onChange={handleEditChange}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        name="status"
                        value={editData.status}
                        onChange={handleEditChange}
                      />
                    </td>
                    <td>
                      <button onClick={handleSaveEdit}>Save</button>
                      <button onClick={() => setEditingClientId(null)}>Cancel</button>
                    </td>
                  </>
                ) : (
                  <>
                    <td>{client.client_name}</td>
                    <td>{client.industry}</td>
                    <td>{client.status}</td>
                    <td>
                      <button onClick={() => handleEdit(client._id)}>Edit</button>
                      <button onClick={() => handleDelete(client._id)}>Delete</button>
                    </td>
                  </>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No clients found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
