import React, { useState } from "react";
import axios from "axios";
import jwtDecode from "jwt-decode"; // Correct import for the default function
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5005/api/login", {
        email,
        password,
      });
      const { token } = response.data;
      localStorage.setItem("jwtToken", token); // Save token in localStorage

      const decodedToken = jwtDecode(token); // Decode the token

      // Check user role and navigate accordingly
      if (decodedToken.role === "admin") {
        window.location.href = "/dashboard";
      } else if (decodedToken.role === "client") {
        // Redirect to client's profile page with clientId
        window.location.href = `/client/${decodedToken.clientId}`;
      }
    } catch (err) {
      setError("Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default Login;
