import React from 'react';
import { useNavigate } from 'react-router-dom';

const Header = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('jwtToken');
  const isAuthenticated = !!token; // Check if the user is authenticated

  // Decode the token to check the role (assuming the token has the role info)
  const decodedToken = token ? JSON.parse(atob(token.split('.')[1])) : null;
  const isClient = decodedToken?.role === 'client'; // Check if the user is a client

  const handleLogout = () => {
    localStorage.removeItem('jwtToken'); // Remove token from localStorage
    navigate('/login'); // Redirect to Login page after logout
  };

  return (
    <header style={headerStyle}>
      <div>
        <h1>Client Management System</h1>
      </div>
      <nav>
        {isAuthenticated ? (
          <>
            {isClient ? (
              <>
                <button onClick={() => navigate(`/client/${decodedToken.clientId}`)} style={linkStyle}>
                  Client Profile
                </button>
                <button onClick={() => navigate(`/contract-management`)} style={linkStyle}>
                  Contract Management
                </button>
                <button onClick={() => navigate(`/activity-tracking`)} style={linkStyle}>
                  Activity Tracking
                </button>
              </>
            ) : (
              <button onClick={() => navigate(`/dashboard`)} style={linkStyle}>
                Dashboard
              </button>
            )}
            <button onClick={handleLogout} style={linkStyle}>Logout</button>
          </>
        ) : (
          <button onClick={() => navigate('/login')} style={linkStyle}>
            Login
          </button>
        )}
      </nav>
    </header>
  );
};

const headerStyle = {
  backgroundColor: '#000',
  color: '#fff',
  padding: '10px 20px',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
};

const linkStyle = {
  color: '#fff',
  textDecoration: 'none',
  fontSize: '16px',
  margin: '0 10px',
};

export default Header;
