import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { TextField, Card, Button, Typography, Table, TableHead, TableBody, TableRow, TableCell, TableContainer, Paper } from "@mui/material";

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    project_name: "",
    description: "",
    start_date: "",
    end_date: "",
    budget: "",
    client_id: clientId,
  });

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    const fetchClientProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/${clientId}`);
        setProjects(response.data);
      } catch (err) {
        setError("Failed to load projects.");
      }
    };

    fetchClientDetails();
    fetchClientProjects();
  }, [clientId]);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5005/api/projects", formData);
      setProjects([...projects, response.data]);
      setShowForm(false);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  if (error) return <Typography color="error">{error}</Typography>;
  if (!client) return <Typography>Loading...</Typography>;

  return (
    <Card sx={{ padding: 3, margin: 3, boxShadow: 3 }}>
      <Typography variant="h4" align="center" gutterBottom>
        Projects for {client.client_name}
      </Typography>
      <TableContainer component={Paper} sx={{ marginBottom: 3 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Project Name</TableCell>
              <TableCell>Description</TableCell>
              <TableCell>Start Date</TableCell>
              <TableCell>End Date</TableCell>
              <TableCell>Budget</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {projects.length === 0 ? (
              <TableRow>
                <TableCell colSpan={5} align="center">No projects found.</TableCell>
              </TableRow>
            ) : (
              projects.map((project) => (
                <TableRow key={project._id}>
                  <TableCell>{project.project_name}</TableCell>
                  <TableCell>{project.description}</TableCell>
                  <TableCell>{new Date(project.start_date).toLocaleDateString()}</TableCell>
                  <TableCell>{new Date(project.end_date).toLocaleDateString()}</TableCell>
                  <TableCell>{project.budget}</TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>
      <Button variant="contained" color="primary" onClick={() => setShowForm(!showForm)}>
        {showForm ? "Cancel" : "Add New Project"}
      </Button>
      {showForm && (
        <Card sx={{ padding: 3, marginTop: 2, boxShadow: 2 }}>
          <Typography variant="h5" gutterBottom>Create a New Project</Typography>
          <form onSubmit={handleFormSubmit}>
            <TextField fullWidth margin="normal" label="Project Name" name="project_name" value={formData.project_name} onChange={handleFormChange} required />
            <TextField fullWidth margin="normal" label="Description" name="description" multiline rows={3} value={formData.description} onChange={handleFormChange} />
            <TextField fullWidth margin="normal" label="Start Date" name="start_date" type="date" InputLabelProps={{ shrink: true }} value={formData.start_date} onChange={handleFormChange} />
            <TextField fullWidth margin="normal" label="End Date" name="end_date" type="date" InputLabelProps={{ shrink: true }} value={formData.end_date} onChange={handleFormChange} />
            <TextField fullWidth margin="normal" label="Budget" name="budget" type="number" value={formData.budget} onChange={handleFormChange} />
            <Button type="submit" variant="contained" color="secondary" fullWidth sx={{ marginTop: 2 }}>Create Project</Button>
          </form>
        </Card>
      )}
    </Card>
  );
};

export default ClientProfile;
