import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import '../App.css';

const ProjectDetailPage = () => {
  const { projectId } = useParams(); // Get the projectId from the URL params
  const [project, setProject] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchProjectDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/${projectId}`);
        console.log(response.data); // Log the data to check its structure
        setProject(response.data);
      } catch (err) {
        setError("Failed to load project details.");
      }
    };

    fetchProjectDetails();
  }, [projectId]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!project) {
    return <div>Loading...</div>;
  }

  return (
    <div className="project-profile-card">
      <h1>{project.project_name}</h1>
      <p><strong>Project Name:</strong> {project.project_name}</p>
      <p><strong>Description:</strong> {project.description || "No description available."}</p>
      <p><strong>Status:</strong> {project.status}</p>
      <p><strong>Budget:</strong> ${project.budget}</p>
      <p><strong>Start Date:</strong> {new Date(project.start_date).toLocaleDateString()}</p>
      <p><strong>End Date:</strong> {project.end_date ? new Date(project.end_date).toLocaleDateString() : "Not defined"}</p>
      <h3>Client Details:</h3>
      <p><strong>Client Name:</strong> {project.client_id.client_name}</p>
      <p><strong>Contact Person:</strong> {project.client_id.contact_person}</p>
      <p><strong>Email:</strong> {project.client_id.email}</p>
      <p><strong>Phone:</strong> {project.client_id.phone}</p>
      <h3>Team Members:</h3>
      {project.team_members && project.team_members.length > 0 ? (
        <ul>
          {project.team_members.map((member, index) => (
            <li key={index}>{member}</li>
          ))}
        </ul>
      ) : (
        <p>No team members assigned.</p>
      )}
    </div>
  );
};

export default ProjectDetailPage;
