import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Box,
  Typography,
  TextField,
  Select,
  MenuItem,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Snackbar,
} from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import Sidebar from '../components/Sidebar';

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState('');
  const [filters, setFilters] = useState({ region: '', industry: '', status: '' });
  const [sortModel, setSortModel] = useState([{ field: 'client_name', sort: 'asc' }]);
  const [authToken] = useState(localStorage.getItem('token') || '');
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [editingClient, setEditingClient] = useState(null);
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  useEffect(() => {
    fetchClients();
  }, [filters, sortModel]);

  const fetchClients = () => {
    axios.get(`http://localhost:5005/api/users`, {
      headers: { Authorization: `Bearer ${authToken}` },
    })
      .then(response => setClients(response.data))
      .catch(err => console.error('Error fetching clients:', err));
  };

  const handleEditClick = (client) => {
    setEditingClient(client);
    setEditDialogOpen(true);
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingClient(prev => ({ ...prev, [name]: value }));
  };

  const handleEditSubmit = () => {
    axios.put(`http://localhost:5005/api/users/${editingClient._id}`, editingClient, {
      headers: { Authorization: `Bearer ${authToken}` },
    }).then(() => {
      fetchClients();
      setEditDialogOpen(false);
      setSnackbarOpen(true);
    }).catch(err => console.error('Error updating client:', err));
  };

  const handleDelete = (clientId) => {
    axios.delete(`http://localhost:5005/api/users/${clientId}`, {
      headers: { Authorization: `Bearer ${authToken}` },
    }).then(() => fetchClients())
      .catch(err => console.error('Error deleting client:', err));
  };

  const columns = [
    { field: 'client_name', headerName: 'Client Name', flex: 1 },
    { field: 'status', headerName: 'Status', flex: 1 },
    { field: 'industry', headerName: 'Industry', flex: 1 },
    { field: 'email', headerName: 'Email', flex: 1 },
    { field: 'phone', headerName: 'Phone', flex: 1 },
    {
      field: 'actions',
      headerName: 'Actions',
      sortable: false,
      renderCell: (params) => (
        <Box>
          <Button size="small" onClick={() => handleEditClick(params.row)}>Edit</Button>
          <Button size="small" color="error" onClick={() => handleDelete(params.row._id)}>Delete</Button>
        </Box>
      ),
      flex: 1,
    },
  ];

  return (
    <Box sx={{ display: 'flex', height: '100vh' }}>
      <Sidebar />
      <Box sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Client Dashboard
        </Typography>
        <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
          <TextField label="Search" variant="outlined" size="small" value={search} onChange={(e) => setSearch(e.target.value)} />
          <Select name="region" value={filters.region} onChange={(e) => setFilters({ ...filters, region: e.target.value })}>
            <MenuItem value="">All Regions</MenuItem>
            <MenuItem value="North America">North America</MenuItem>
            <MenuItem value="Europe">Europe</MenuItem>
          </Select>
          <Select name="industry" value={filters.industry} onChange={(e) => setFilters({ ...filters, industry: e.target.value })}>
            <MenuItem value="">All Industries</MenuItem>
            <MenuItem value="Finance">Finance</MenuItem>
            <MenuItem value="Technology">Technology</MenuItem>
          </Select>
        </Box>
        <DataGrid rows={clients} columns={columns} pageSize={5} sortingMode="server" sortModel={sortModel} onSortModelChange={setSortModel} autoHeight />
      </Box>
      {/* Edit Dialog */}
      <Dialog open={editDialogOpen} onClose={() => setEditDialogOpen(false)}>
        <DialogTitle>Edit Client</DialogTitle>
        <DialogContent>
          <TextField name="client_name" label="Client Name" fullWidth margin="dense" value={editingClient?.client_name || ''} onChange={handleEditChange} />
          <TextField name="email" label="Email" fullWidth margin="dense" value={editingClient?.email || ''} onChange={handleEditChange} />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleEditSubmit} color="primary">Save</Button>
        </DialogActions>
      </Dialog>
      {/* Snackbar Notification */}
      <Snackbar open={snackbarOpen} autoHideDuration={3000} message="Client updated successfully" onClose={() => setSnackbarOpen(false)} />
    </Box>
  );
};

export default Dashboard;
