import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ContractManagementPage = ({ clientId }) => {
  const [projects, setProjects] = useState([]);
  const [formData, setFormData] = useState({
    project_name: '',
    description: '',
    start_date: '',
    end_date: '',
    status: '',
    budget: '',
    team_members: '',
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editProjectId, setEditProjectId] = useState(null);

  // Fetch projects for the given client
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/projects/${clientId}`);
        setProjects(response.data);
      } catch (error) {
        console.error('Error fetching projects:', error);
      }
    };

    fetchProjects();
  }, [clientId]);

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        // Update project
        await axios.put(`http://localhost:5000/api/projects/${editProjectId}`, formData);
        setProjects((prev) =>
          prev.map((project) =>
            project._id === editProjectId ? { ...project, ...formData } : project
          )
        );
        setIsEditing(false);
        setEditProjectId(null);
      } else {
        // Add new project
        const response = await axios.post('http://localhost:5000/api/projects', {
          ...formData,
          client_id: clientId,
        });
        setProjects((prev) => [...prev, response.data]);
      }
      setFormData({
        project_name: '',
        description: '',
        start_date: '',
        end_date: '',
        status: '',
        budget: '',
        team_members: '',
      });
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleDelete = async (projectId) => {
    try {
      await axios.delete(`http://localhost:5000/api/projects/${projectId}`);
      setProjects((prev) => prev.filter((project) => project._id !== projectId));
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };


  // Handle edit project
  const handleEdit = (project) => {
    setFormData({
      project_name: project.project_name,
      description: project.description,
      start_date: project.start_date.slice(0, 10),
      end_date: project.end_date.slice(0, 10),
      status: project.status,
      budget: project.budget,
      team_members: project.team_members.join(', '),
    });
    setIsEditing(true);
    setEditProjectId(project._id);
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">Contract Management</h1>

      {/* Form Section */}
      <form className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-6" onSubmit={handleFormSubmit}>
        <h2 className="text-xl font-semibold mb-4">{isEditing ? 'Edit Project' : 'Add Project'}</h2>
        <div className="grid grid-cols-2 gap-4">
          <input
            type="text"
            name="project_name"
            placeholder="Project Name"
            value={formData.project_name}
            onChange={handleInputChange}
            required
            className="border p-2 rounded"
          />
          <input
            type="text"
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleInputChange}
            required
            className="border p-2 rounded"
          />
          <input
            type="date"
            name="start_date"
            value={formData.start_date}
            onChange={handleInputChange}
            required
            className="border p-2 rounded"
          />
          <input
            type="date"
            name="end_date"
            value={formData.end_date}
            onChange={handleInputChange}
            className="border p-2 rounded"
          />
          <select
            name="status"
            value={formData.status}
            onChange={handleInputChange}
            required
            className="border p-2 rounded"
          >
            <option value="" disabled>
              Select Status
            </option>
            <option value="Ongoing">Ongoing</option>
            <option value="Completed">Completed</option>
            <option value="On Hold">On Hold</option>
          </select>
          <input
            type="number"
            name="budget"
            placeholder="Budget"
            value={formData.budget}
            onChange={handleInputChange}
            required
            className="border p-2 rounded"
          />
          <input
            type="text"
            name="team_members"
            placeholder="Team Members (comma-separated)"
            value={formData.team_members}
            onChange={handleInputChange}
            required
            className="border p-2 rounded"
          />
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded mt-4"
        >
          {isEditing ? 'Update Project' : 'Add Project'}
        </button>
      </form>

      {/* Projects Table */}
      <h2 className="text-xl font-semibold mb-4">Projects</h2>
      <table className="min-w-full bg-white border">
        <thead>
          <tr>
            <th className="py-2 px-4 border">Name</th>
            <th className="py-2 px-4 border">Description</th>
            <th className="py-2 px-4 border">Status</th>
            <th className="py-2 px-4 border">Actions</th>
          </tr>
        </thead>
        <tbody>
          {projects.map((project) => (
            <tr key={project._id}>
              <td className="py-2 px-4 border">{project.project_name}</td>
              <td className="py-2 px-4 border">{project.description}</td>
              <td className="py-2 px-4 border">{project.status}</td>
              <td className="py-2 px-4 border">
                <button
                  onClick={() => handleEdit(project)}
                  className="bg-yellow-500 text-white px-2 py-1 rounded mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(project._id)}
                  className="bg-red-500 text-white px-2 py-1 rounded"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ContractManagementPage;
