import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import {
  Button,
  Card,
  TextField,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Modal,
  CircularProgress,
} from "@mui/material";
import axios from "axios";

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    project_name: "",
    description: "",
    start_date: "",
    end_date: "",
    budget: "",
    client_id: clientId,
  });

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    const fetchClientProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/${clientId}`);
        setProjects(response.data);
      } catch (err) {
        setError("Failed to load projects.");
      } finally {
        setLoading(false);
      }
    };

    fetchClientDetails();
    fetchClientProjects();
  }, [clientId]);

  const handleFormChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  }, []);

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5005/api/projects", formData);
      setProjects([...projects, response.data]);
      setShowForm(false);
    } catch (error) {
      setError("Error creating project.");
    }
  };

  if (loading) {
    return <CircularProgress sx={{ display: "block", margin: "20px auto" }} />;
  }

  if (error) {
    return <Typography color="error">{error}</Typography>;
  }

  return (
    <div className="p-6">
      <Typography variant="h4" align="center" gutterBottom className="text-primary font-bold">
        Projects for {client.client_name}
      </Typography>
      <Card className="p-6 shadow-lg rounded-lg">
        {projects.length === 0 ? (
          <Typography className="text-gray-500">No projects found for this client.</Typography>
        ) : (
          <TableContainer component={Paper} className="shadow-md rounded-lg">
            <Table>
              <TableHead>
                <TableRow className="bg-primary text-white">
                  <TableCell className="font-bold">Project Name</TableCell>
                  <TableCell className="font-bold">Description</TableCell>
                  <TableCell className="font-bold">Start Date</TableCell>
                  <TableCell className="font-bold">End Date</TableCell>
                  <TableCell className="font-bold">Budget</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {projects.map((project) => (
                  <TableRow key={project._id} className="hover:bg-gray-100">
                    <TableCell>{project.project_name}</TableCell>
                    <TableCell>{project.description}</TableCell>
                    <TableCell>{new Date(project.start_date).toLocaleDateString()}</TableCell>
                    <TableCell>{new Date(project.end_date).toLocaleDateString()}</TableCell>
                    <TableCell>${project.budget}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
        <Button
          variant="contained"
          color="primary"
          onClick={() => setShowForm(!showForm)}
          className="mt-4"
        >
          {showForm ? "Cancel" : "Add New Project"}
        </Button>
        <Modal open={showForm} onClose={() => setShowForm(false)}>
          <Card className="p-6 w-[500px] mx-auto mt-20 shadow-lg rounded-lg">
            <Typography variant="h6" gutterBottom>
              Create a New Project
            </Typography>
            <form onSubmit={handleFormSubmit}>
              <TextField
                fullWidth
                label="Project Name"
                name="project_name"
                value={formData.project_name}
                onChange={handleFormChange}
                margin="normal"
                required
              />
              <TextField
                fullWidth
                label="Description"
                name="description"
                value={formData.description}
                onChange={handleFormChange}
                margin="normal"
                multiline
              />
              <TextField
                fullWidth
                label="Start Date"
                type="date"
                name="start_date"
                value={formData.start_date}
                onChange={handleFormChange}
                margin="normal"
                InputLabelProps={{ shrink: true }}
              />
              <TextField
                fullWidth
                label="End Date"
                type="date"
                name="end_date"
                value={formData.end_date}
                onChange={handleFormChange}
                margin="normal"
                InputLabelProps={{ shrink: true }}
              />
              <TextField
                fullWidth
                label="Budget"
                type="number"
                name="budget"
                value={formData.budget}
                onChange={handleFormChange}
                margin="normal"
              />
              <Button
                variant="contained"
                color="primary"
                type="submit"
                className="mt-4 w-full"
              >
                Create Project
              </Button>
            </form>
          </Card>
        </Modal>
      </Card>
    </div>
  );
};

export default ClientProfile;
