import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import {
  Container,
  TextField,
  Select,
  MenuItem,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Typography,
  Modal,
  Box,
} from "@mui/material";
import PendingUsers from "../components/PendingUser";
import "./Dashboard.css";

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState("");
  const [filters, setFilters] = useState({
    region: "",
    industry: "",
    status: "",
  });
  const [sortOrder, setSortOrder] = useState("asc");
  const [sortColumn, setSortColumn] = useState("client_name");
  const [authToken] = useState(localStorage.getItem("token") || "");
  const [editingClient, setEditingClient] = useState(null);
  const [error, setError] = useState("");
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    fetchClients();
  }, [filters, sortColumn, sortOrder]);

  const fetchClients = () => {
    const query = new URLSearchParams({
      search,
      ...filters,
      sortColumn,
      sortOrder,
    }).toString();

    axios
      .get(`http://localhost:5005/api/users?${query}`, {
        headers: { Authorization: `Bearer ${authToken}` },
      })
      .then((response) => setClients(response.data))
      .catch((err) => console.error("Error fetching clients:", err));
  };

  const handleSort = (column) => {
    setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    setSortColumn(column);
  };

  const handleEditClick = (client) => {
    setEditingClient(client);
    setModalOpen(true);
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingClient({ ...editingClient, [name]: value });
  };

  const handleEditSubmit = async () => {
    try {
      await axios.put(
        `http://localhost:5005/api/users/${editingClient._id}`,
        editingClient,
        { headers: { Authorization: `Bearer ${authToken}` } }
      );
      setClients(
        clients.map((client) =>
          client._id === editingClient._id ? editingClient : client
        )
      );
      setModalOpen(false);
    } catch (err) {
      console.error("Error updating client:", err);
      setError("Failed to update client. Please try again.");
    }
  };

  return (
    <Container>
      <PendingUsers />
      <Typography variant="h4" gutterBottom>
        Clients
      </Typography>

      <TextField
        label="Search clients"
        variant="outlined"
        fullWidth
        margin="normal"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <Box display="flex" gap={2} mb={3}>
        <Select
          name="region"
          value={filters.region}
          onChange={(e) => setFilters({ ...filters, region: e.target.value })}
          displayEmpty
          fullWidth
        >
          <MenuItem value="">All Regions</MenuItem>
          <MenuItem value="North America">North America</MenuItem>
          <MenuItem value="Europe">Europe</MenuItem>
          <MenuItem value="Asia">Asia</MenuItem>
        </Select>

        <Select
          name="industry"
          value={filters.industry}
          onChange={(e) => setFilters({ ...filters, industry: e.target.value })}
          displayEmpty
          fullWidth
        >
          <MenuItem value="">All Industries</MenuItem>
          <MenuItem value="Finance">Finance</MenuItem>
          <MenuItem value="Technology">Technology</MenuItem>
          <MenuItem value="Healthcare">Healthcare</MenuItem>
        </Select>
      </Box>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell onClick={() => handleSort("client_name")}>
                Client Name
              </TableCell>
              <TableCell>Status</TableCell>
              <TableCell>Industry</TableCell>
              <TableCell>Email</TableCell>
              <TableCell>Phone</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {clients.map((client) => (
              <TableRow key={client._id}>
                <TableCell>
                  <Link to={`/client/${client._id}`}>{client.client_name}</Link>
                </TableCell>
                <TableCell>{client.status}</TableCell>
                <TableCell>{client.industry}</TableCell>
                <TableCell>{client.email}</TableCell>
                <TableCell>{client.phone}</TableCell>
                <TableCell>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleEditClick(client)}
                  >
                    Edit
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Edit Modal */}
      <Modal open={modalOpen} onClose={() => setModalOpen(false)}>
        <Box className="modal-box">
          <Typography variant="h6">Edit Client</Typography>
          <TextField
            label="Client Name"
            name="client_name"
            fullWidth
            value={editingClient?.client_name || ""}
            onChange={handleEditChange}
          />
          <Button onClick={handleEditSubmit} variant="contained" color="primary">
            Save
          </Button>
        </Box>
      </Modal>
    </Container>
  );
};

export default Dashboard;
