import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const ClientProfile = () => {
  const { clientId } = useParams(); // Get the clientId from the URL params
  const navigate = useNavigate();
  const [client, setClient] = useState(null);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [updatedClientData, setUpdatedClientData] = useState({});

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    fetchClientDetails();
  }, [clientId]);

  const handleEditClick = () => {
    setIsEditing(true);
    setUpdatedClientData(client);  // Pre-fill form with current data
  };

  const handleChange = (e) => {
    setUpdatedClientData({ ...updatedClientData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:5005/api/client/${clientId}`, updatedClientData);
      const { updatedClient, token } = response.data;

      // Save the new token in localStorage
      localStorage.setItem("token", token);

      // Update the client data in state
      setClient(updatedClient);
      setIsEditing(false);
      alert("Profile updated successfully!");
    } catch (err) {
      setError("Failed to update profile");
    }
  };

  if (error) {
    return <div>{error}</div>;
  }

  if (!client) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Client Profile</h1>
      {isEditing ? (
        <form onSubmit={handleSubmit}>
          <label>Name:</label>
          <input
            type="text"
            name="client_name"
            value={updatedClientData.client_name}
            onChange={handleChange}
          />
          <br />
          <label>Contact Person:</label>
          <input
            type="text"
            name="contact_person"
            value={updatedClientData.contact_person}
            onChange={handleChange}
          />
          <br />
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={updatedClientData.email}
            onChange={handleChange}
          />
          <br />
          <label>Phone:</label>
          <input
            type="text"
            name="phone"
            value={updatedClientData.phone}
            onChange={handleChange}
          />
          <br />
          <label>Industry:</label>
          <input
            type="text"
            name="industry"
            value={updatedClientData.industry}
            onChange={handleChange}
          />
          <br />
          <button type="submit">Save Changes</button>
        </form>
      ) : (
        <>
          <p><strong>Name:</strong> {client.client_name}</p>
          <p><strong>Contact Person:</strong> {client.contact_person}</p>
          <p><strong>Email:</strong> {client.email}</p>
          <p><strong>Phone:</strong> {client.phone}</p>
          <p><strong>Industry:</strong> {client.industry}</p>
          <button onClick={handleEditClick}>Edit Profile</button>
        </>
      )}
    </div>
  );
};

export default ClientProfile;
