import React, { useState } from "react";
import axios from "axios";
import { jwtDecode } from 'jwt-decode'; // Use named import
 // Correct import for jwt-decode
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5005/api/login", {
        email,
        password,
      });
      const { token } = response.data;
      localStorage.setItem("jwtToken", token); // Save token in localStorage

      const decodedToken = jwt_decode(token); // Decode the token

      // Check user role and navigate accordingly
      if (decodedToken.role === "admin") {
        window.location.href = "/dashboard";
      } else if (decodedToken.role === "client") {
        // Redirect to client's profile page with clientId
        window.location.href = `/client/${decodedToken.clientId}`;
      }
    } catch (err) {
      setError("Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            className="input-field"
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            className="input-field"
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button className="submit-btn" type="submit">Login</button>
        </form>
        {error && <p className="error-message">{error}</p>}
      </div>
    </div>
  );
};

export default Login;
