import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Button, Table, Modal, Input, Form, Typography, message, Spin, Card, Alert } from "antd";
import { EditOutlined, PlusOutlined } from "@ant-design/icons";
import "antd/dist/reset.css";

const { Title } = Typography;

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [editProject, setEditProject] = useState(null);
  const [form] = Form.useForm();
  const [clientForm] = Form.useForm();

  // Added state for showing/hiding the client edit form
  const [showEditClientForm, setShowEditClientForm] = useState(false); 

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
        clientForm.setFieldsValue(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    const fetchClientProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/${clientId}`);
        setProjects(response.data);
      } catch (err) {
        setError("Failed to load projects.");
      } finally {
        setLoading(false);
      }
    };

    fetchClientDetails();
    fetchClientProjects();
  }, [clientId]);

  const handleFormSubmit = async () => {
    try {
      const formData = await form.validateFields();
      if (editProject) {
        // Update project
        await axios.put(`http://localhost:5005/api/projects/${editProject._id}`, formData);
        setProjects(projects.map((p) => (p._id === editProject._id ? { ...p, ...formData } : p)));
      } else {
        // Create new project
        const response = await axios.post("http://localhost:5005/api/projects", {
          ...formData,
          client_id: clientId,
        });
        setProjects([...projects, response.data]);
      }
      setShowForm(false);
      setEditProject(null);
      form.resetFields();
      message.success("Project saved successfully");
    } catch (error) {
      message.error("Error saving project");
    }
  };

  const handleClientUpdate = async () => {
    try {
      const formData = await clientForm.validateFields();
      await axios.put(`http://localhost:5005/api/client/${clientId}`, formData);
      setClient(formData);
      setShowEditClientForm(false);
      message.success("Client details updated successfully");
    } catch (error) {
      message.error("Error updating client details");
    }
  };

  if (loading) return <Spin size="large" style={{ display: "block", margin: "auto" }} />;
  if (error) return <Alert message={error} type="error" showIcon />;
  if (!client) return <Typography>Loading...</Typography>;

  return (
    <div style={{ padding: "24px" }}>
      <Card>
        <Title level={3}>
          {client.client_name} <EditOutlined onClick={() => setShowEditClientForm(true)} style={{ cursor: "pointer", marginLeft: 10 }} />
        </Title>
        <Table
          dataSource={projects}
          rowKey="_id"
          columns={[
            { title: "Project Name", dataIndex: "project_name" },
            { title: "Description", dataIndex: "description" },
            { title: "Start Date", dataIndex: "start_date", render: (date) => new Date(date).toLocaleDateString() },
            { title: "End Date", dataIndex: "end_date", render: (date) => new Date(date).toLocaleDateString() },
            { title: "Budget", dataIndex: "budget" },
            {
              title: "Actions",
              render: (_, record) => (
                <Button icon={<EditOutlined />} onClick={() => { setEditProject(record); setShowForm(true); form.setFieldsValue(record); }} />
              ),
            },
          ]}
        />
        <Button type="primary" icon={<PlusOutlined />} onClick={() => { setEditProject(null); setShowForm(true); }} style={{ marginTop: "20px" }}>
          Add New Project
        </Button>
      </Card>

      <Modal title="Edit Client Details" open={showEditClientForm} onCancel={() => setShowEditClientForm(false)} onOk={handleClientUpdate}>
        <Form form={clientForm} layout="vertical">
          <Form.Item name="client_name" label="Client Name" rules={[{ required: true, message: "Please enter a client name" }]}> <Input /> </Form.Item>
          <Form.Item name="client_email" label="Client Email" rules={[{ required: true, message: "Please enter a valid email" }]}> <Input type="email" /> </Form.Item>
        </Form>
      </Modal>

      <Modal
        title={editProject ? "Edit Project" : "Create a New Project"}
        open={showForm}
        onCancel={() => setShowForm(false)}
        onOk={handleFormSubmit}
        okText={editProject ? "Update Project" : "Create Project"}
      >
        <Form form={form} layout="vertical">
          <Form.Item name="project_name" label="Project Name" rules={[{ required: true, message: "Please enter a project name" }]}> <Input /> </Form.Item>
          <Form.Item name="description" label="Description"> <Input.TextArea /> </Form.Item>
          <Form.Item name="start_date" label="Start Date" rules={[{ required: true, message: "Please select a start date" }]}> <Input type="date" /> </Form.Item>
          <Form.Item name="end_date" label="End Date" rules={[{ required: true, message: "Please select an end date" }]}> <Input type="date" /> </Form.Item>
          <Form.Item name="budget" label="Budget"> <Input type="number" /> </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default ClientProfile;
