import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Card, CardContent, Typography, Avatar, Grid } from "@mui/material";
import { Business, Email, Phone, LocationOn, AccountCircle } from "@mui/icons-material";
import { motion } from "framer-motion";
import "../App.css";
import Chatbot from "./Chatbot";

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [error, setError] = useState("");
  const [showChatbot, setShowChatbot] = useState(false);

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    fetchClientDetails();
  }, [clientId]);

  if (error) return <Typography color="error">{error}</Typography>;
  if (!client) return <Typography>Loading...</Typography>;

  return (
    <motion.div
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="dashboard-container"
    >
      <Card className="client-profile-card" elevation={6}>
        <CardContent>
          <Grid container spacing={2} alignItems="center">
            <Grid item>
              <Avatar sx={{ bgcolor: "#1976d2", width: 80, height: 80 }}>
                <AccountCircle sx={{ fontSize: 50 }} />
              </Avatar>
            </Grid>
            <Grid item>
              <Typography variant="h4" fontWeight="bold">
                {client.client_name}
              </Typography>
              <Typography color="textSecondary">{client.industry}</Typography>
            </Grid>
          </Grid>
          <Typography variant="body1" mt={2}>
            <Business /> <strong>Contact Person:</strong> {client.contact_person}
          </Typography>
          <Typography variant="body1">
            <Email /> <strong>Email:</strong> {client.email}
          </Typography>
          <Typography variant="body1">
            <Phone /> <strong>Phone:</strong> {client.phone}
          </Typography>
          <Typography variant="body1">
            <LocationOn /> <strong>Address:</strong> {client?.address?.street}, {client?.address?.city}, {client?.address?.state} {client?.address?.postal_code}, {client?.address?.country}
          </Typography>
        </CardContent>
      </Card>

      {/* Chatbot Toggle Button */}
      <div className="chatbot-toggle" onClick={() => setShowChatbot(!showChatbot)}>
        <img src="/bot.png" alt="Chat" className="chat-icon" />
      </div>
      {showChatbot && <Chatbot />}
    </motion.div>
  );
};

export default ClientProfile;
