import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import {
  Button,
  Card,
  TextField,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Modal,
} from "@mui/material";
import axios from "axios";

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    project_name: "",
    description: "",
    start_date: "",
    end_date: "",
    budget: "",
    client_id: clientId,
  });

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    const fetchClientProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/${clientId}`);
        setProjects(response.data);
      } catch (err) {
        setError("Failed to load projects.");
      }
    };

    fetchClientDetails();
    fetchClientProjects();
  }, [clientId]);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5005/api/projects", formData);
      setProjects([...projects, response.data]);
      setShowForm(false);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  if (error) {
    return <Typography color="error">{error}</Typography>;
  }

  if (!client) {
    return <Typography>Loading...</Typography>;
  }

  return (
    <div style={{ padding: "24px", maxWidth: "900px", margin: "auto" }}>
      <Typography variant="h4" align="center" gutterBottom>
        Projects for {client.client_name}
      </Typography>
      <Card style={{ padding: "20px", borderRadius: "12px", boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)" }}>
        {projects.length === 0 ? (
          <Typography>No projects found for this client.</Typography>
        ) : (
          <TableContainer component={Paper} style={{ borderRadius: "12px" }}>
            <Table>
              <TableHead>
                <TableRow style={{ backgroundColor: "#1976d2", color: "white" }}>
                  <TableCell style={{ color: "white", fontWeight: "bold" }}>Project Name</TableCell>
                  <TableCell style={{ color: "white", fontWeight: "bold" }}>Description</TableCell>
                  <TableCell style={{ color: "white", fontWeight: "bold" }}>Start Date</TableCell>
                  <TableCell style={{ color: "white", fontWeight: "bold" }}>End Date</TableCell>
                  <TableCell style={{ color: "white", fontWeight: "bold" }}>Budget</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {projects.map((project) => (
                  <TableRow key={project._id}>
                    <TableCell>{project.project_name}</TableCell>
                    <TableCell>{project.description}</TableCell>
                    <TableCell>{new Date(project.start_date).toLocaleDateString()}</TableCell>
                    <TableCell>{new Date(project.end_date).toLocaleDateString()}</TableCell>
                    <TableCell>${project.budget}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
        <Button
          variant="contained"
          color="primary"
          onClick={() => setShowForm(!showForm)}
          style={{ marginTop: "20px", borderRadius: "8px" }}
        >
          {showForm ? "Cancel" : "Add New Project"}
        </Button>
        <Modal open={showForm} onClose={() => setShowForm(false)}>
          <Card style={{ padding: "24px", maxWidth: "500px", margin: "auto", marginTop: "10%", borderRadius: "12px" }}>
            <Typography variant="h6" gutterBottom>
              Create a New Project
            </Typography>
            <form onSubmit={handleFormSubmit}>
              <TextField fullWidth label="Project Name" name="project_name" value={formData.project_name} onChange={handleFormChange} margin="normal" required />
              <TextField fullWidth label="Description" name="description" value={formData.description} onChange={handleFormChange} margin="normal" multiline />
              <TextField fullWidth label="Start Date" type="date" name="start_date" value={formData.start_date} onChange={handleFormChange} margin="normal" InputLabelProps={{ shrink: true }} />
              <TextField fullWidth label="End Date" type="date" name="end_date" value={formData.end_date} onChange={handleFormChange} margin="normal" InputLabelProps={{ shrink: true }} />
              <TextField fullWidth label="Budget" type="number" name="budget" value={formData.budget} onChange={handleFormChange} margin="normal" />
              <Button variant="contained" color="primary" type="submit" style={{ marginTop: "20px", borderRadius: "8px" }}>
                Create Project
              </Button>
            </form>
          </Card>
        </Modal>
      </Card>
    </div>
  );
};

export default ClientProfile;
