import React, { useState } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { Container, TextField, Button, Typography, Alert, Box, Card, CardContent, CircularProgress, Grid, Divider } from "@mui/material";

const AdminLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const response = await axios.post("http://localhost:5005/api/login", { email, password });
      const { token } = response.data;
      localStorage.setItem("jwtToken", token);

      const decodedToken = jwtDecode(token);
      if (decodedToken.role === "admin") {
        window.location.href = "/dashboard";
      } else {
        setError("Unauthorized access.");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Invalid credentials. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 12, display: "flex", justifyContent: "center", alignItems: "center" }}>
      <Card sx={{ boxShadow: 8, borderRadius: 4, px: 4, py: 5, width: "100%" }}>
        <CardContent>
          <Typography variant="h4" fontWeight="bold" gutterBottom align="center" color="primary">
            Admin Login
          </Typography>
          <Divider sx={{ mb: 3 }} />
          {error && <Alert severity="error" sx={{ mb: 3 }}>{error}</Alert>}
          <Box component="form" onSubmit={handleSubmit} sx={{ display: "flex", flexDirection: "column", gap: 3 }}>
            <TextField
              label="Email Address"
              type="email"
              variant="outlined"
              fullWidth
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              autoFocus
            />
            <TextField
              label="Password"
              type="password"
              variant="outlined"
              fullWidth
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
              sx={{ height: 48, fontSize: 18, fontWeight: "bold", textTransform: "none" }}
              disabled={loading}
            >
              {loading ? <CircularProgress size={26} color="inherit" /> : "Sign In"}
            </Button>
          </Box>
          <Grid container justifyContent="flex-end" sx={{ mt: 2 }}>
            <Typography variant="body2" color="textSecondary">
              Forgot password?
            </Typography>
          </Grid>
        </CardContent>
      </Card>
    </Container>
  );
};

export default AdminLogin;
