import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { motion } from "framer-motion";
import {
  Button,
  Card,
  TextField,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Modal,
} from "@mui/material";
import axios from "axios";

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    project_name: "",
    description: "",
    start_date: "",
    end_date: "",
    budget: "",
    client_id: clientId,
  });

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    const fetchClientProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/${clientId}`);
        setProjects(response.data);
      } catch (err) {
        setError("Failed to load projects.");
      }
    };

    fetchClientDetails();
    fetchClientProjects();
  }, [clientId]);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5005/api/projects", formData);
      setProjects([...projects, response.data]);
      setShowForm(false);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  if (error) {
    return <Typography color="error">{error}</Typography>;
  }

  if (!client) {
    return <Typography>Loading...</Typography>;
  }

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }} style={{ padding: "24px" }}>
      <Typography variant="h4" align="center" gutterBottom>
        Projects for {client.client_name}
      </Typography>
      <motion.div initial={{ y: -20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.5 }}>
        <Card style={{ padding: "20px" }}>
          {projects.length === 0 ? (
            <Typography>No projects found for this client.</Typography>
          ) : (
            <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.7 }}>
              <TableContainer component={Paper}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Project Name</TableCell>
                      <TableCell>Description</TableCell>
                      <TableCell>Start Date</TableCell>
                      <TableCell>End Date</TableCell>
                      <TableCell>Budget</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {projects.map((project) => (
                      <TableRow key={project._id}>
                        <TableCell>{project.project_name}</TableCell>
                        <TableCell>{project.description}</TableCell>
                        <TableCell>{new Date(project.start_date).toLocaleDateString()}</TableCell>
                        <TableCell>{new Date(project.end_date).toLocaleDateString()}</TableCell>
                        <TableCell>{project.budget}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </motion.div>
          )}
          <Button
            variant="contained"
            color="primary"
            onClick={() => setShowForm(!showForm)}
            style={{ marginTop: "20px" }}
          >
            {showForm ? "Cancel" : "Add New Project"}
          </Button>
          <Modal open={showForm} onClose={() => setShowForm(false)}>
            <motion.div
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.3 }}
              style={{ padding: "20px", maxWidth: "500px", margin: "auto", marginTop: "10%" }}
            >
              <Card>
                <Typography variant="h6" gutterBottom>
                  Create a New Project
                </Typography>
                <form onSubmit={handleFormSubmit}>
                  <TextField
                    fullWidth
                    label="Project Name"
                    name="project_name"
                    value={formData.project_name}
                    onChange={handleFormChange}
                    margin="normal"
                    required
                  />
                  <TextField
                    fullWidth
                    label="Description"
                    name="description"
                    value={formData.description}
                    onChange={handleFormChange}
                    margin="normal"
                    multiline
                  />
                  <TextField
                    fullWidth
                    label="Start Date"
                    type="date"
                    name="start_date"
                    value={formData.start_date}
                    onChange={handleFormChange}
                    margin="normal"
                    InputLabelProps={{ shrink: true }}
                  />
                  <TextField
                    fullWidth
                    label="End Date"
                    type="date"
                    name="end_date"
                    value={formData.end_date}
                    onChange={handleFormChange}
                    margin="normal"
                    InputLabelProps={{ shrink: true }}
                  />
                  <TextField
                    fullWidth
                    label="Budget"
                    type="number"
                    name="budget"
                    value={formData.budget}
                    onChange={handleFormChange}
                    margin="normal"
                  />
                  <Button variant="contained" color="primary" type="submit" style={{ marginTop: "20px" }}>
                    Create Project
                  </Button>
                </form>
              </Card>
            </motion.div>
          </Modal>
        </Card>
      </motion.div>
    </motion.div>
  );
};

export default ClientProfile;
