import React, { useState, useEffect } from "react";
import axios from "axios";

const ClientManagement = () => {
  const [clients, setClients] = useState([]);
  const [editingClient, setEditingClient] = useState(null);
  const [formData, setFormData] = useState({ email: "", role: "", password: "" });

  // Fetch clients on mount
  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get("http://localhost:5005/api/users");
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  // Handle Delete
  const handleDelete = async (clientId) => {
    try {
      await axios.delete(`http://localhost:5005/api/client/${clientId}`);
      setClients(clients.filter((client) => client._id !== clientId));
    } catch (error) {
      console.error("Error deleting client:", error);
    }
  };

  // Handle Edit
  const handleEdit = (client) => {
    setEditingClient(client);
    setFormData({ email: client.email, role: client.role, password: "" });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(
        `http://localhost:5005/api/client/${editingClient._id}`,
        formData
      );
      console.log("Update response:", response.data);
      fetchClients(); // Refresh the client list
      setEditingClient(null);
      setFormData({ email: "", role: "", password: "" });
    } catch (error) {
      console.error("Error updating client:", error);
    }
  };

  return (
    <div>
      <h1>Client Management</h1>

      {/* List of Clients */}
      <ul>
        {clients.map((client) => (
          <li key={client._id}>
            {client.email} ({client.role})
            <button onClick={() => handleEdit(client)}>Edit</button>
            <button onClick={() => handleDelete(client._id)}>Delete</button>
          </li>
        ))}
      </ul>

      {/* Edit Form */}
      {editingClient && (
        <form onSubmit={handleUpdate}>
          <h2>Edit Client</h2>
          <label>Email:</label>
          <input
            type="email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          />
          <label>Role:</label>
          <input
            type="text"
            value={formData.role}
            onChange={(e) => setFormData({ ...formData, role: e.target.value })}
          />
          <label>Password:</label>
          <input
            type="password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          />
          <button type="submit">Update</button>
          <button type="button" onClick={() => setEditingClient(null)}>
            Cancel
          </button>
        </form>
      )}
    </div>
  );
};

export default ClientManagement;
