import React, { useState, useEffect } from "react";
import axios from "axios";

const PendingUsers = () => {
  const [pendingUsers, setPendingUsers] = useState([]);

  useEffect(() => {
    fetch("/api/pending-users")
      .then((response) => response.json())
      .then((data) => {
        setPendingUsers(data); // Make sure you are setting the state correctly
      })
      .catch((error) => console.error("Error fetching pending users:", error));
  }, []);

  // Function to approve a user
  const approveUser = async (userId) => {
    try {
      const response = await axios.put(`http://localhost:5005/api/approve-user/${userId}`);
      alert(response.data.message);
      setPendingUsers(pendingUsers.filter((user) => user._id !== userId)); // Remove approved user from the list
    } catch (err) {
      console.error("Error approving user:", err);
      alert("Failed to approve user");
    }
  };

  // Function to reject a user
  const rejectUser = async (userId) => {
    try {
      const response = await axios.delete(`http://localhost:5005/api/reject-user/${userId}`);
      alert(response.data.message);
      setPendingUsers(pendingUsers.filter((user) => user._id !== userId)); // Remove rejected user from the list
    } catch (err) {
      console.error("Error rejecting user:", err);
      alert("Failed to reject user");
    }
  };

  return (
    <div>
      <h2>Pending Users</h2>
      {pendingUsers.length === 0 ? (
        <p>No pending users</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Client Name</th>
              <th>Contact Person</th>
              <th>Email</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {pendingUsers.map((user) => (
              <tr key={user._id}>
                <td>{user.client_name}</td>
                <td>{user.contact_person}</td>
                <td>{user.email}</td>
                <td>
                  <button onClick={() => approveUser(user._id)}>Approve</button>
                  <button onClick={() => rejectUser(user._id)}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PendingUsers;
