import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ProjectDetailPage = () => {
  const { projectId } = useParams(); // Get the projectId from the URL
  const [project, setProject] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProjectDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/${projectId}`);
        setProject(response.data);
      } catch (err) {
        setError('Failed to fetch project details');
      }
    };

    fetchProjectDetails();
  }, [projectId]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!project) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{project.project_name}</h1>
      <p><strong>Project Description:</strong> {project.description}</p>
      <p><strong>Status:</strong> {project.status}</p>
      <p><strong>Start Date:</strong> {new Date(project.start_date).toLocaleDateString()}</p>
      <p><strong>End Date:</strong> {new Date(project.end_date).toLocaleDateString()}</p>
      <p><strong>Budget:</strong> ${project.budget}</p>
      <p><strong>Team Members:</strong> {project.team_members.join(', ')}</p>
    </div>
  );
};

export default ProjectDetailPage;
