import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState('');
  const [filters, setFilters] = useState({
    region: '',
    industry: '',
    status: ''
  });
  const [sortOrder, setSortOrder] = useState('asc');
  const [sortColumn, setSortColumn] = useState('client_name');
  const [authToken, setAuthToken] = useState(localStorage.getItem('token') || '');

  const [editClientId, setEditClientId] = useState(null); // Track which client is being edited
  const [editedClientData, setEditedClientData] = useState({}); // Store edited client data

  useEffect(() => {
    fetchClients();
  }, [filters, sortColumn, sortOrder]);

  const fetchClients = () => {
    const query = new URLSearchParams({
      search,
      region: filters.region,
      industry: filters.industry,
      status: filters.status,
      sortColumn,
      sortOrder
    });

    axios
      .get(`http://localhost:5005/api/users?${query.toString()}`, {
        headers: { Authorization: `Bearer ${authToken}` }
      })
      .then((response) => {
        setClients(response.data);
      })
      .catch((err) => {
        console.error('Error fetching clients:', err);
      });
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleSort = (column) => {
    const newSortOrder = sortOrder === 'asc' ? 'desc' : 'asc';
    setSortOrder(newSortOrder);
    setSortColumn(column);
  };

  const handleEdit = (client) => {
    setEditClientId(client._id); // Set the client ID to indicate editing mode
    setEditedClientData(client); // Populate the edited client data
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedClientData({ ...editedClientData, [name]: value });
  };

  const handleSave = (clientId) => {
    axios
      .put(`http://localhost:5005/api/users/${clientId}`, editedClientData, {
        headers: { Authorization: `Bearer ${authToken}` }
      })
      .then(() => {
        setClients(clients.map((client) => (client._id === clientId ? { ...client, ...editedClientData } : client)));
        setEditClientId(null); // Exit edit mode after saving
        alert('Client updated successfully');
      })
      .catch((err) => {
        console.error('Error updating client:', err);
        alert('Failed to update client');
      });
  };

  const handleDelete = (clientId) => {
    axios
      .delete(`http://localhost:5005/api/users/${clientId}`, {
        headers: { Authorization: `Bearer ${authToken}` }
      })
      .then(() => {
        setClients(clients.filter((client) => client._id !== clientId));
        alert('Client deleted successfully');
      })
      .catch((err) => {
        console.error('Error deleting client:', err);
        alert('Failed to delete client');
      });
  };

  const exportData = (format) => {
    alert(`Exporting data as ${format}`);
  };

  return (
    <div className="dashboard">
      <h1>Client Dashboard</h1>

      {/* Search Bar */}
      <input type="text" placeholder="Search clients..." value={search} onChange={handleSearch} />

      {/* Filter Options */}
      <select name="region" onChange={handleFilterChange} value={filters.region}>
        <option value="">All Regions</option>
        <option value="North America">North America</option>
        <option value="Europe">Europe</option>
        <option value="Asia">Asia</option>
      </select>

      <select name="industry" onChange={handleFilterChange} value={filters.industry}>
        <option value="">All Industries</option>
        <option value="Finance">Finance</option>
        <option value="Technology">Technology</option>
        <option value="Healthcare">Healthcare</option>
      </select>

      <select name="status" onChange={handleFilterChange} value={filters.status}>
        <option value="">All Statuses</option>
        <option value="Active">Active</option>
        <option value="Inactive">Inactive</option>
      </select>

      {/* Export Buttons */}
      <button onClick={() => exportData('excel')}>Export as Excel</button>
      <button onClick={() => exportData('pdf')}>Export as PDF</button>
      <button onClick={() => exportData('csv')}>Export as CSV</button>

      {/* Client Data Table */}
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort('client_name')}>Client Name</th>
            <th onClick={() => handleSort('industry')}>Industry</th>
            <th onClick={() => handleSort('status')}>Status</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Contact Person</th>
            <th>Location</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {clients.length > 0 ? (
            clients.map((client) => (
              <tr key={client._id}>
                <td>
                  {editClientId === client._id ? (
                    <input
                      type="text"
                      name="client_name"
                      value={editedClientData.client_name || ''}
                      onChange={handleInputChange}
                    />
                  ) : (
                    client.client_name
                  )}
                </td>
                <td>
                  {editClientId === client._id ? (
                    <input
                      type="text"
                      name="industry"
                      value={editedClientData.industry || ''}
                      onChange={handleInputChange}
                    />
                  ) : (
                    client.industry
                  )}
                </td>
                <td>
                  {editClientId === client._id ? (
                    <select
                      name="status"
                      value={editedClientData.status || ''}
                      onChange={handleInputChange}
                    >
                      <option value="Active">Active</option>
                      <option value="Inactive">Inactive</option>
                    </select>
                  ) : (
                    client.status
                  )}
                </td>
                <td>
                  {editClientId === client._id ? (
                    <input
                      type="email"
                      name="email"
                      value={editedClientData.email || ''}
                      onChange={handleInputChange}
                    />
                  ) : (
                    client.email
                  )}
                </td>
                <td>
                  {editClientId === client._id ? (
                    <input
                      type="text"
                      name="phone"
                      value={editedClientData.phone || ''}
                      onChange={handleInputChange}
                    />
                  ) : (
                    client.phone
                  )}
                </td>
                <td>
                  {editClientId === client._id ? (
                    <input
                      type="text"
                      name="contact_person"
                      value={editedClientData.contact_person || ''}
                      onChange={handleInputChange}
                    />
                  ) : (
                    client.contact_person
                  )}
                </td>
                <td>
                  {editClientId === client._id ? (
                    <input
                      type="text"
                      name="address"
                      value={editedClientData.address || ''}
                      onChange={handleInputChange}
                    />
                  ) : (
                    `${client.address ? client.address.street : 'No address available'}`
                  )}
                </td>
                <td>
                  {editClientId === client._id ? (
                    <button onClick={() => handleSave(client._id)}>Save</button>
                  ) : (
                    <>
                      <button onClick={() => handleEdit(client)}>Edit</button>
                      <button onClick={() => handleDelete(client._id)}>Delete</button>
                    </>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No clients found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
