import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import AdminLogin from './components/AdminLogin';
import ClientLogin from './components/ClientLogin';
import Dashboard from './components/Dashboard';
import ClientProfile from './components/ClientProfile';
import HomePage from './components/HomePage';
import ContractManagement from './components/ContractManagement';
import ActivityTracking from './components/ActivityTracking';
import Header from './components/Header';
import Footer from './components/Footer';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [role, setRole] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (token) {
      try {
        const decodedToken = JSON.parse(atob(token.split('.')[1])); // Decode the JWT
        setRole(decodedToken.role); // Set user role from the decoded token
        setIsAuthenticated(true);
      } catch (error) {
        console.error("Invalid token", error);
        setIsAuthenticated(false);
      }
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('jwtToken');
    setIsAuthenticated(false);
    setRole(null); // Reset the role on logout
  };

  return (
    <Router>
      <Header onLogout={handleLogout} />
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<HomePage />} />
        <Route
          path="/admin-login"
          element={isAuthenticated && role === 'admin' ? <Navigate to="/dashboard" /> : <AdminLogin />}
        />
        <Route
          path="/client-login"
          element={isAuthenticated && role === 'client' ? <Navigate to={`/client/${role.clientId}`} /> : <ClientLogin />}
        />

        {/* Protected Routes */}
        <Route
          path="/dashboard"
          element={isAuthenticated && role === 'admin' ? <Dashboard /> : <Navigate to="/admin-login" />}
        />
        <Route
          path="/client/:clientId"
          element={isAuthenticated && role === 'client' ? <ClientProfile /> : <Navigate to="/client-login" />}
        />
        <Route
          path="/contract-management"
          element={isAuthenticated && role === 'client' ? <ContractManagement /> : <Navigate to="/client-login" />}
        />
        <Route
          path="/activity-tracking"
          element={isAuthenticated && role === 'client' ? <ActivityTracking /> : <Navigate to="/client-login" />}
        />

        {/* Fallback Route */}
        <Route path="*" element={<h2>404 - Page Not Found</h2>} />
      </Routes>
      <Footer />
    </Router>
  );
};

export default App;
