import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Paper, Typography, TextField, Select, MenuItem, IconButton, Button, Dialog, DialogActions, DialogContent, DialogTitle, InputLabel, FormControl, Grid } from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import { Edit, Delete } from '@mui/icons-material';
import PendingUsers from '../components/PendingUser';

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState('');
  const [filters, setFilters] = useState({ region: '', industry: '', status: '' });
  const [editingClient, setEditingClient] = useState(null);
  const [error, setError] = useState('');
  const authToken = localStorage.getItem('token') || '';

  useEffect(() => {
    fetchClients();
  }, [filters, search]);

  const fetchClients = () => {
    axios.get(`http://localhost:5005/api/users`, {
      headers: { Authorization: `Bearer ${authToken}` },
      params: { ...filters, search },
    })
    .then((res) => setClients(res.data))
    .catch((err) => console.error('Error fetching clients:', err));
  };

  const handleDelete = (clientId) => {
    axios.delete(`http://localhost:5005/api/users/${clientId}`, {
      headers: { Authorization: `Bearer ${authToken}` },
    })
    .then(() => setClients(clients.filter((client) => client._id !== clientId)))
    .catch((err) => console.error('Error deleting client:', err));
  };

  const handleEditSubmit = async () => {
    try {
      await axios.put(`http://localhost:5005/api/users/${editingClient._id}`, editingClient, {
        headers: { Authorization: `Bearer ${authToken}` },
      });
      setClients(clients.map((c) => (c._id === editingClient._id ? editingClient : c)));
      setEditingClient(null);
    } catch (err) {
      console.error('Error updating client:', err);
      setError('Failed to update client. Please try again.');
    }
  };

  const columns = [
    { field: 'client_name', headerName: 'Client Name', width: 200 },
    { field: 'status', headerName: 'Status', width: 120 },
    { field: 'industry', headerName: 'Industry', width: 150 },
    { field: 'email', headerName: 'Email', width: 200 },
    { field: 'phone', headerName: 'Phone', width: 150 },
    { field: 'contact_person', headerName: 'Contact Person', width: 180 },
    {
      field: 'actions',
      headerName: 'Actions',
      width: 120,
      renderCell: (params) => (
        <>
          <IconButton onClick={() => setEditingClient(params.row)} color="primary">
            <Edit />
          </IconButton>
          <IconButton onClick={() => handleDelete(params.row._id)} color="secondary">
            <Delete />
          </IconButton>
        </>
      ),
    },
  ];

  return (
    <Container>
      <PendingUsers />
      <Typography variant="h4" gutterBottom>Client Dashboard</Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} sm={4}>
          <TextField fullWidth label="Search" value={search} onChange={(e) => setSearch(e.target.value)} />
        </Grid>
        <Grid item xs={12} sm={3}>
          <FormControl fullWidth>
            <InputLabel>Region</InputLabel>
            <Select name="region" value={filters.region} onChange={(e) => setFilters({ ...filters, region: e.target.value })}>
              <MenuItem value="">All Regions</MenuItem>
              <MenuItem value="North America">North America</MenuItem>
              <MenuItem value="Europe">Europe</MenuItem>
              <MenuItem value="Asia">Asia</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} sm={3}>
          <FormControl fullWidth>
            <InputLabel>Industry</InputLabel>
            <Select name="industry" value={filters.industry} onChange={(e) => setFilters({ ...filters, industry: e.target.value })}>
              <MenuItem value="">All Industries</MenuItem>
              <MenuItem value="Finance">Finance</MenuItem>
              <MenuItem value="Technology">Technology</MenuItem>
              <MenuItem value="Healthcare">Healthcare</MenuItem>
            </Select>
          </FormControl>
        </Grid>
      </Grid>
      <Paper sx={{ height: 400, width: '100%', marginTop: 3 }}>
        <DataGrid rows={clients} columns={columns} getRowId={(row) => row._id} pageSize={5} />
      </Paper>

      {editingClient && (
        <Dialog open={Boolean(editingClient)} onClose={() => setEditingClient(null)}>
          <DialogTitle>Edit Client</DialogTitle>
          <DialogContent>
            <TextField fullWidth margin="dense" label="Client Name" value={editingClient.client_name} onChange={(e) => setEditingClient({ ...editingClient, client_name: e.target.value })} />
            <TextField fullWidth margin="dense" label="Contact Person" value={editingClient.contact_person} onChange={(e) => setEditingClient({ ...editingClient, contact_person: e.target.value })} />
            <TextField fullWidth margin="dense" label="Email" value={editingClient.email} onChange={(e) => setEditingClient({ ...editingClient, email: e.target.value })} />
            <TextField fullWidth margin="dense" label="Phone" value={editingClient.phone} onChange={(e) => setEditingClient({ ...editingClient, phone: e.target.value })} />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setEditingClient(null)}>Cancel</Button>
            <Button onClick={handleEditSubmit} color="primary">Save</Button>
          </DialogActions>
          {error && <Typography color="error">{error}</Typography>}
        </Dialog>
      )}
    </Container>
  );
};

export default Dashboard;
