import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import ClientProfile from './components/ClientProfile';
import HomePage from './components/HomePage';
import ContractManagement from './components/ContractManagement'; 
import ActivityTracking from './components/ActivityTracking';
import Header from './components/Header';
import Footer from './components/Footer';

const App = () => {
  const token = localStorage.getItem('jwtToken'); // Get token from localStorage
  const isAuthenticated = !!token; // Check if the user is authenticated

  return (
    <Router>
      <Header />
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={isAuthenticated ? <Navigate to="/dashboard" /> : <Login />} />

        {/* Protected Routes */}
        <Route
          path="/dashboard"
          element={isAuthenticated ? <Dashboard /> : <Navigate to="/login" />}
        />
        <Route
          path="/client/:clientId"
          element={isAuthenticated ? <ClientProfile /> : <Navigate to="/login" />}
        />
        <Route
          path="/contract-management"
          element={isAuthenticated ? <ContractManagement /> : <Navigate to="/login" />}
        />
        <Route
          path="/activity-tracking"
          element={isAuthenticated ? <ActivityTracking /> : <Navigate to="/login" />}
        />

        {/* Fallback Route */}
        <Route path="*" element={<h2>404 - Page Not Found</h2>} />
      </Routes>
      <Footer />
    </Router>
  );
};

export default App;
