import React from "react";
import {
  Box,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  Container,
  Avatar,
  Divider,
} from "@mui/material";
import {
  PlayCircleOutline,
  Settings,
  Person,
  Code,
  Security,
  Dashboard,
  TrendingUp,
  Sync,
  Storage,
  SupportAgent,
} from "@mui/icons-material";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import "./HomePage.css";

const fadeIn = {
  hidden: { opacity: 0, y: 50 },
  visible: { opacity: 1, y: 0, transition: { duration: 1, ease: "easeOut" } },
};

const cardVariants = {
  hidden: { opacity: 0, scale: 0.8 },
  visible: { opacity: 1, scale: 1, transition: { duration: 0.6 } },
};

const HomePage = () => {
  const navigate = useNavigate();

  return (
    <Box
      sx={{
        minHeight: "100vh",
        bgcolor: "linear-gradient(to right, #0D1B2A, #1B263B, #415A77)",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        color: "#fff",
        py: 6,
      }}
    >
      {/* Hero Section */}
      <motion.div initial="hidden" animate="visible" variants={fadeIn}>
        <Typography variant="h3" fontWeight="bold" gutterBottom>
          Elevate Your Business with Our CMS
        </Typography>
        <Typography variant="h5" sx={{ maxWidth: 700, mx: "auto", opacity: 0.9 }}>
          AI-driven client management, real-time analytics, automation, and secure storage.
        </Typography>
        <motion.div whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.95 }}>
          <Button
            variant="contained"
            sx={{
              mt: 4,
              px: 5,
              py: 1.5,
              fontSize: "1.2rem",
              bgcolor: "#FFD700",
              color: "#000",
              fontWeight: "bold",
              "&:hover": { bgcolor: "#FFC107" },
            }}
            onClick={() => navigate("/client-login")}
          >
            Get Started
          </Button>
        </motion.div>
      </motion.div>

      {/* Features Section */}
      <Container sx={{ mt: 8 }}>
        <Grid container spacing={4} justifyContent="center">
          {[
            { icon: <PlayCircleOutline fontSize="large" />, text: "Quick Setup", desc: "Get started in minutes with an intuitive interface." },
            { icon: <Settings fontSize="large" />, text: "AI Automation", desc: "Reduce manual tasks with smart automation." },
            { icon: <Dashboard fontSize="large" />, text: "Analytics Dashboard", desc: "Track key metrics and performance." },
            { icon: <Person fontSize="large" />, text: "Client Management", desc: "Efficiently organize and manage clients." },
            { icon: <TrendingUp fontSize="large" />, text: "Revenue Insights", desc: "Monitor earnings and optimize business strategies." },
            { icon: <Security fontSize="large" />, text: "Enterprise Security", desc: "Ensure data protection with encryption." },
            { icon: <Sync fontSize="large" />, text: "Seamless Integrations", desc: "Integrate with popular tools and platforms." },
            { icon: <Storage fontSize="large" />, text: "Cloud Backup", desc: "Keep your data safe and accessible." },
            { icon: <SupportAgent fontSize="large" />, text: "24/7 Support", desc: "Reliable support whenever you need it." },
          ].map((feature, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <motion.div initial="hidden" animate="visible" variants={cardVariants}>
                <Card sx={{ p: 3, textAlign: "center", boxShadow: 4, bgcolor: "#1E1E1E", color: "#fff", borderRadius: 3 }}>
                  {feature.icon}
                  <CardContent>
                    <Typography variant="h6" fontWeight="bold">{feature.text}</Typography>
                    <Typography variant="body2" sx={{ opacity: 0.7 }}>{feature.desc}</Typography>
                  </CardContent>
                </Card>
              </motion.div>
            </Grid>
          ))}
        </Grid>
      </Container>

      {/* Call-to-Action Banner */}
      <Box
        sx={{
          mt: 10,
          width: "100%",
          py: 4,
          bgcolor: "#FFD700",
          textAlign: "center",
          color: "#000",
          fontWeight: "bold",
        }}
      >
        <Typography variant="h5">
          Ready to scale your business with the best CMS?
        </Typography>
        <Button
          variant="contained"
          sx={{ mt: 2, px: 4, bgcolor: "#000", color: "#FFD700", "&:hover": { bgcolor: "#222" } }}
          onClick={() => navigate("/client-login")}
        >
          Get Started Now
        </Button>
      </Box>
    </Box>
  );
};

export default HomePage;
