import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ClientProfile = () => {
  const { clientId } = useParams(); // Get the clientId from the URL params
  const [client, setClient] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    fetchClientDetails();
  }, [clientId]);

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gray-100">
        <p className="text-red-600 font-semibold">{error}</p>
      </div>
    );
  }

  if (!client) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gray-100">
        <p className="text-blue-600 font-semibold">Loading...</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 bg-white rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-gray-800 mb-4">Client Profile</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="space-y-4">
          <p className="text-lg text-gray-700"><strong>Name:</strong> {client.client_name}</p>
          <p className="text-lg text-gray-700"><strong>Contact Person:</strong> {client.contact_person}</p>
          <p className="text-lg text-gray-700"><strong>Email:</strong> {client.email}</p>
          <p className="text-lg text-gray-700"><strong>Phone:</strong> {client.phone}</p>
          <p className="text-lg text-gray-700"><strong>Industry:</strong> {client.industry}</p>
          <p className="text-lg text-gray-700"><strong>Status:</strong> {client.status}</p>
        </div>
        <div className="space-y-4">
          <h3 className="text-xl font-semibold text-gray-800">Address:</h3>
          <p className="text-lg text-gray-700">{client.address.street}</p>
          <p className="text-lg text-gray-700">{client.address.city}, {client.address.state} {client.address.postal_code}</p>
          <p className="text-lg text-gray-700">{client.address.country}</p>
        </div>
      </div>
    </div>
  );
};

export default ClientProfile;


