import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";  // Use useNavigate instead of useHistory
import axios from "axios";
import './App.css';  // Ensure correct path for the CSS file

const ClientProfileWithProjects = () => {
  const { clientId, projectId } = useParams(); // Get clientId and projectId from URL params
  const navigate = useNavigate(); // Replaced useHistory with useNavigate
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [formData, setFormData] = useState({
    project_name: "",
    description: "",
    start_date: "",
    end_date: "",
    status: "",
    budget: "",
    team_members: [],
  });
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    const fetchProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/${clientId}`);
        setProjects(response.data);
      } catch (err) {
        setError("Failed to load projects.");
      }
    };

    if (clientId) {
      fetchClientDetails();
      fetchProjects();
    }

    if (projectId) {
      // Fetch project data for editing
      const fetchProjectDetails = async () => {
        try {
          const response = await axios.get(`http://localhost:5005/api/projects/${projectId}`);
          setFormData(response.data);
        } catch (err) {
          console.error("Failed to load project details.");
        }
      };
      fetchProjectDetails();
    }
  }, [clientId, projectId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (projectId) {
        await axios.put(`http://localhost:5005/api/projects/${projectId}`, formData);
      } else {
        await axios.post(`http://localhost:5005/api/projects`, { ...formData, client_id: clientId });
      }
      navigate(`/projects/${clientId}`);  // Redirect to the projects list page
    } catch (err) {
      console.error("Error submitting form:", err);
    }
  };

  if (error) {
    return <div>{error}</div>;
  }

  if (!client) {
    return <div>Loading client details...</div>;
  }

  return (
    <div>
      <h1>{client.client_name}'s Profile</h1>
      <p><strong>Name:</strong> {client.client_name}</p>
      <p><strong>Contact Person:</strong> {client.contact_person}</p>
      <p><strong>Email:</strong> {client.email}</p>
      <p><strong>Phone:</strong> {client.phone}</p>
      <p><strong>Industry:</strong> {client.industry}</p>
      <p><strong>Status:</strong> {client.status}</p>
      <h3>Address:</h3>
      {client?.address ? (
        <>
          <p>{client.address.street}</p>
          <p>{client.address.city}, {client.address.state} {client.address.postal_code}</p>
          <p>{client.address.country}</p>
        </>
      ) : (
        <p>Address information is unavailable.</p>
      )}

      <h2>Projects for Client {client.client_name}</h2>
      <ul>
        {projects.length > 0 ? (
          projects.map((project) => (
            <li key={project._id}>
              <p>{project.project_name}</p>
              <button onClick={() => navigate(`/edit-project/${clientId}/${project._id}`)}>Edit</button>
            </li>
          ))
        ) : (
          <p>No projects found for this client.</p>
        )}
      </ul>
      <button onClick={() => navigate(`/add-project/${clientId}`)}>Add New Project</button>

      <h3>{projectId ? "Edit Project" : "Add New Project"}</h3>
      <form onSubmit={handleFormSubmit}>
        <input
          type="text"
          name="project_name"
          value={formData.project_name}
          onChange={handleInputChange}
          placeholder="Project Name"
          required
        />
        <textarea
          name="description"
          value={formData.description}
          onChange={handleInputChange}
          placeholder="Project Description"
          required
        />
        <input
          type="date"
          name="start_date"
          value={formData.start_date}
          onChange={handleInputChange}
        />
        <input
          type="date"
          name="end_date"
          value={formData.end_date}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="status"
          value={formData.status}
          onChange={handleInputChange}
          placeholder="Project Status"
        />
        <input
          type="number"
          name="budget"
          value={formData.budget}
          onChange={handleInputChange}
          placeholder="Project Budget"
        />
        <button type="submit">{projectId ? "Update" : "Add"} Project</button>
      </form>
    </div>
  );
};

export default ClientProfileWithProjects;
