import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const ClientProfile = () => {
  const { clientId } = useParams(); // Get the clientId from the URL params
  const [client, setClient] = useState(null);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [updatedClient, setUpdatedClient] = useState({});
  const navigate = useNavigate(); // Use navigate instead of useHistory

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        setClient(response.data);
        setUpdatedClient(response.data); // Initialize updatedClient with the fetched data
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    fetchClientDetails();
  }, [clientId]);

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setUpdatedClient((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmitEdit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5005/api/client/${clientId}`, updatedClient);
      setIsEditing(false); // Switch to view mode after successful update
      setClient(updatedClient); // Update client data with the edited values
    } catch (err) {
      setError("Failed to update client details.");
    }
  };

  if (error) {
    return <div>{error}</div>;
  }

  if (!client) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Client Profile</h1>
      {isEditing ? (
        <form onSubmit={handleSubmitEdit}>
          <div>
            <label>
              <strong>Name:</strong>
              <input
                type="text"
                name="client_name"
                value={updatedClient.client_name}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <div>
            <label>
              <strong>Contact Person:</strong>
              <input
                type="text"
                name="contact_person"
                value={updatedClient.contact_person}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <div>
            <label>
              <strong>Email:</strong>
              <input
                type="email"
                name="email"
                value={updatedClient.email}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <div>
            <label>
              <strong>Phone:</strong>
              <input
                type="text"
                name="phone"
                value={updatedClient.phone}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <div>
            <label>
              <strong>Industry:</strong>
              <input
                type="text"
                name="industry"
                value={updatedClient.industry}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <div>
            <label>
              <strong>Status:</strong>
              <input
                type="text"
                name="status"
                value={updatedClient.status}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <h3>Address:</h3>
          <div>
            <label>
              <strong>Street:</strong>
              <input
                type="text"
                name="address.street"
                value={updatedClient.address.street}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <div>
            <label>
              <strong>City:</strong>
              <input
                type="text"
                name="address.city"
                value={updatedClient.address.city}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <div>
            <label>
              <strong>State:</strong>
              <input
                type="text"
                name="address.state"
                value={updatedClient.address.state}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <div>
            <label>
              <strong>Postal Code:</strong>
              <input
                type="text"
                name="address.postal_code"
                value={updatedClient.address.postal_code}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <div>
            <label>
              <strong>Country:</strong>
              <input
                type="text"
                name="address.country"
                value={updatedClient.address.country}
                onChange={handleEditChange}
              />
            </label>
          </div>
          <button type="submit">Save Changes</button>
          <button type="button" onClick={() => setIsEditing(false)}>
            Cancel
          </button>
        </form>
      ) : (
        <>
          <p><strong>Name:</strong> {client.client_name}</p>
          <p><strong>Contact Person:</strong> {client.contact_person}</p>
          <p><strong>Email:</strong> {client.email}</p>
          <p><strong>Phone:</strong> {client.phone}</p>
          <p><strong>Industry:</strong> {client.industry}</p>
          <p><strong>Status:</strong> {client.status}</p>
          <h3>Address:</h3>
          <p>{client.address.street}</p>
          <p>{client.address.city}, {client.address.state} {client.address.postal_code}</p>
          <p>{client.address.country}</p>
          <button onClick={() => setIsEditing(true)}>Edit Profile</button>
        </>
      )}
    </div>
  );
};

export default ClientProfile;
