import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { 
  Container, Typography, Table, TableBody, TableCell, 
  TableContainer, TableHead, TableRow, Paper, Button, 
  TextField, Card, CardContent, CircularProgress, Alert, Grid 
} from "@mui/material";
import { Input } from "@/components/ui/input";
import { Card as ShadCard } from "@/components/ui/card";

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    project_name: "",
    description: "",
    start_date: "",
    end_date: "",
    budget: "",
    client_id: clientId,
  });

  useEffect(() => {
    const fetchClientData = async () => {
      try {
        const clientRes = await axios.get(`http://localhost:5005/api/client/${clientId}`);
        const projectsRes = await axios.get(`http://localhost:5005/api/projects/${clientId}`);
        setClient(clientRes.data);
        setProjects(projectsRes.data);
      } catch (err) {
        setError("Failed to load client details or projects.");
      } finally {
        setLoading(false);
      }
    };
    fetchClientData();
  }, [clientId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5005/api/projects", formData);
      setProjects([...projects, response.data]);
      setShowForm(false);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  if (loading) return <CircularProgress sx={{ display: "block", margin: "auto" }} />;
  if (error) return <Alert severity="error">{error}</Alert>;

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Card sx={{ mb: 4, p: 3 }}>
        <Typography variant="h4" textAlign="center">
          {client?.client_name}'s Projects
        </Typography>
      </Card>

      <TableContainer component={Paper} sx={{ mb: 4 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><b>Project Name</b></TableCell>
              <TableCell><b>Description</b></TableCell>
              <TableCell><b>Start Date</b></TableCell>
              <TableCell><b>End Date</b></TableCell>
              <TableCell><b>Budget</b></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {projects.map((project) => (
              <TableRow key={project._id}>
                <TableCell>{project.project_name}</TableCell>
                <TableCell>{project.description}</TableCell>
                <TableCell>{new Date(project.start_date).toLocaleDateString()}</TableCell>
                <TableCell>{new Date(project.end_date).toLocaleDateString()}</TableCell>
                <TableCell>${project.budget}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Button 
        variant="contained" 
        color="primary" 
        onClick={() => setShowForm(!showForm)}
        sx={{ mb: 2 }}
      >
        {showForm ? "Cancel" : "Add New Project"}
      </Button>

      {showForm && (
        <ShadCard className="p-4 shadow-md border">
          <Typography variant="h6">Create a New Project</Typography>
          <form onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <Input placeholder="Project Name" name="project_name" onChange={handleChange} required />
              </Grid>
              <Grid item xs={12}>
                <Input placeholder="Description" name="description" onChange={handleChange} required />
              </Grid>
              <Grid item xs={6}>
                <TextField fullWidth type="date" name="start_date" label="Start Date" onChange={handleChange} />
              </Grid>
              <Grid item xs={6}>
                <TextField fullWidth type="date" name="end_date" label="End Date" onChange={handleChange} />
              </Grid>
              <Grid item xs={12}>
                <Input placeholder="Budget" type="number" name="budget" onChange={handleChange} required />
              </Grid>
              <Grid item xs={12}>
                <Button type="submit" variant="contained" color="success" fullWidth>
                  Create Project
                </Button>
              </Grid>
            </Grid>
          </form>
        </ShadCard>
      )}
    </Container>
  );
};

export default ClientProfile;
