import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const ProjectsPage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch projects from the backend
    const fetchProjects = async () => {
      try {
        const response = await axios.get("http://localhost:5005/api/projects"); // Replace with your API endpoint
        setProjects(response.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch projects. Please try again later.");
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  const handleViewDetails = (projectId) => {
    navigate(`/project/${projectId}`);
  };

  const handleApprove = async (projectId) => {
    try {
      await axios.patch(`http://localhost:5005/api/projects/${projectId}`, { status: "Approved" });
      setProjects((prevProjects) =>
        prevProjects.map((project) =>
          project._id === projectId ? { ...project, status: "Approved" } : project
        )
      );
    } catch (err) {
      console.error("Failed to approve project:", err);
    }
  };

  const handleReject = async (projectId) => {
    try {
      await axios.patch(`http://localhost:5005/api/projects/${projectId}`, { status: "Rejected" });
      setProjects((prevProjects) =>
        prevProjects.map((project) =>
          project._id === projectId ? { ...project, status: "Rejected" } : project
        )
      );
    } catch (err) {
      console.error("Failed to reject project:", err);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={pageStyle}>
      <h1>All Projects</h1>
      {projects.length === 0 ? (
        <p>No projects found.</p>
      ) : (
        <table style={tableStyle}>
          <thead>
            <tr>
              <th>Project ID</th>
              <th>Project Name</th>
              <th>Client</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {projects.map((project) => (
              <tr key={project._id}>
                <td>{project._id}</td>
                <td>{project.project_name}</td>
                <td>{project.client_id.client_name}</td>
                <td>{project.status}</td>
                <td>
                  <button onClick={() => handleViewDetails(project._id)} style={buttonStyle}>
                    View Details
                  </button>
                  <button
                    onClick={() => handleApprove(project._id)}
                    style={{ ...buttonStyle, backgroundColor: "#28a745" }}
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => handleReject(project._id)}
                    style={{ ...buttonStyle, backgroundColor: "#dc3545" }}
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const pageStyle = {
  padding: "20px",
  fontFamily: "Arial, sans-serif",
};

const tableStyle = {
  width: "100%",
  borderCollapse: "collapse",
  marginTop: "20px",
};

const buttonStyle = {
  padding: "10px 20px",
  margin: "10px 5px",
  border: "none",
  borderRadius: "4px",
  backgroundColor: "#739ea7",
  color: "white",
  cursor: "pointer",
  fontSize: "14px",
};

export default ProjectsPage;
