import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const ProjectsPage = () => {
  const [pendingProjects, setPendingProjects] = useState([]);
  const [approvedProjects, setApprovedProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editProject, setEditProject] = useState(null);
  const [formData, setFormData] = useState({
    project_name: '',
    client_id: '',
    status: 'Pending',
  });
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch projects from the backend
    const fetchProjects = async () => {
      try {
        const response = await axios.get("http://localhost:5005/api/projects");
        const allProjects = response.data;

        // Separate projects into pending and approved
        setPendingProjects(allProjects.filter(project => !project.isApproved));
        setApprovedProjects(allProjects.filter(project => project.isApproved));

        setLoading(false);
      } catch (err) {
        setError("Failed to fetch projects. Please try again later.");
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  const handleViewDetails = (projectId) => {
    navigate(`/project/${projectId}`);
  };

  const handleApprove = async (projectId) => {
    try {
      await axios.put(`http://localhost:5005/api/projects/${projectId}/approve`);
      alert("Project approved successfully");
      setPendingProjects(prev => prev.filter(project => project._id !== projectId));
      const approvedProject = pendingProjects.find(project => project._id === projectId);
      setApprovedProjects(prev => [...prev, { ...approvedProject, isApproved: true }]);
    } catch (err) {
      console.error("Error approving project:", err);
      alert("Failed to approve project");
    }
  };

  const handleReject = async (projectId) => {
    try {
      await axios.put(`http://localhost:5005/api/projects/${projectId}/reject`);
      alert("Project rejected successfully");
      setPendingProjects(prev => prev.filter(project => project._id !== projectId));
    } catch (err) {
      console.error("Error rejecting project:", err);
      alert("Failed to reject project");
    }
  };

  const handleStatusChange = async (projectId, status) => {
    try {
      await axios.put(`http://localhost:5005/api/projects/${projectId}`, { status });
      alert("Project status updated successfully");
      setApprovedProjects(prevProjects =>
        prevProjects.map(project =>
          project._id === projectId ? { ...project, status: status } : project
        )
      );
    } catch (err) {
      console.error("Error updating project status:", err);
      alert("Failed to update project status");
    }
  };

  const handleEdit = (project) => {
    setEditProject(project);
    setFormData({
      project_name: project.project_name,
      client_id: project.client_id._id,
      status: project.status,
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmitEdit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5005/api/projects/${editProject._id}`, formData);
      alert("Project updated successfully");
      setApprovedProjects(prev =>
        prev.map(project =>
          project._id === editProject._id ? { ...project, ...formData } : project
        )
      );
      setEditProject(null);
      setFormData({ project_name: '', client_id: '', status: 'Pending' });
    } catch (err) {
      console.error("Error updating project:", err);
      alert("Failed to update project");
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={pageStyle}>
      <h2>Pending Projects</h2>
      {pendingProjects.length === 0 ? (
        <p>No pending projects found.</p>
      ) : (
        <table style={tableStyle}>
          <thead>
            <tr>
              <th>Project ID</th>
              <th>Project Name</th>
              <th>Client</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {pendingProjects.map((project) => (
              <tr key={project._id}>
                <td>{project._id}</td>
                <td>{project.project_name}</td>
                <td>{project.client_id.client_name}</td>
                <td>
                  <button onClick={() => handleApprove(project._id)} style={{ ...buttonStyle, backgroundColor: "#28a745" }}>
                    Approve
                  </button>
                  <button onClick={() => handleReject(project._id)} style={{ ...buttonStyle, backgroundColor: "#dc3545" }}>
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <h2>Approved Projects</h2>
      {approvedProjects.length === 0 ? (
        <p>No approved projects found.</p>
      ) : (
        <table style={tableStyle}>
          <thead>
            <tr>
              <th>Project ID</th>
              <th>Project Name</th>
              <th>Client</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {approvedProjects.map((project) => (
              <tr key={project._id}>
                <td>{project._id}</td>
                <td>{project.project_name}</td>
                <td>{project.client_id.client_name}</td>
                <td>
                  <select
                    value={project.status}
                    onChange={(e) => handleStatusChange(project._id, e.target.value)}
                    style={selectStyle}
                  >
                    <option value="Pending">Pending</option>
                    <option value="Ongoing">Ongoing</option>
                    <option value="Completed">Completed</option>
                  </select>
                </td>
                <td>
                  <button onClick={() => handleViewDetails(project._id)} style={buttonStyle}>
                    View Details
                  </button>
                  <button onClick={() => handleEdit(project)} style={{ ...buttonStyle, backgroundColor: "#ffc107" }}>
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Edit Project Form */}
      {editProject && (
        <div style={editFormStyle}>
          <h3>Edit Project</h3>
          <form onSubmit={handleSubmitEdit}>
            <label>
              Project Name:
              <input
                type="text"
                name="project_name"
                value={formData.project_name}
                onChange={handleInputChange}
                required
              />
            </label>
            <label>
              Client ID:
              <input
                type="text"
                name="client_id"
                value={formData.client_id}
                onChange={handleInputChange}
                required
              />
            </label>
            <label>
              Status:
              <select
                name="status"
                value={formData.status}
                onChange={handleInputChange}
                required
              >
                <option value="Pending">Pending</option>
                <option value="Ongoing">Ongoing</option>
                <option value="Completed">Completed</option>
              </select>
            </label>
            <button type="submit" style={{ ...buttonStyle, backgroundColor: "#007bff" }}>
              Update Project
            </button>
            <button type="button" onClick={() => setEditProject(null)} style={buttonStyle}>
              Cancel
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

const pageStyle = {
  padding: "20px",
  fontFamily: "Arial, sans-serif",
};

const tableStyle = {
  width: "100%",
  borderCollapse: "collapse",
  marginTop: "20px",
  border: "1px solid #ccc",
};

const buttonStyle = {
  padding: "10px 20px",
  margin: "10px 5px",
  border: "none",
  borderRadius: "4px",
  backgroundColor: "#739ea7",
  color: "white",
  cursor: "pointer",
  fontSize: "14px",
};

const selectStyle = {
  padding: "5px 10px",
  border: "1px solid #ccc",
  borderRadius: "4px",
  backgroundColor: "#fff",
};

const editFormStyle = {
  padding: "20px",
  border: "1px solid #ccc",
  marginTop: "20px",
  borderRadius: "4px",
  backgroundColor: "#f8f9fa",
};

export default ProjectsPage;
