import React from "react";
import { Box, Typography, Button, Grid, Card, CardContent } from "@mui/material";
import { PlayCircleOutline, Settings, Person, Code } from "@mui/icons-material";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

const fadeIn = {
  hidden: { opacity: 0, y: 30 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8 } }
};

const cardVariants = {
  hidden: { opacity: 0, scale: 0.9 },
  visible: { opacity: 1, scale: 1, transition: { duration: 0.5 } }
};

const HomePage = () => {
  const navigate = useNavigate();

  return (
    <Box
      sx={{
        minHeight: "100vh",
        bgcolor: "#f5f5f5",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        p: 4,
        textAlign: "center"
      }}
    >
      {/* Hero Section */}
      <motion.div initial="hidden" animate="visible" variants={fadeIn}>
        <Typography variant="h2" fontWeight="bold" color="primary" gutterBottom>
          CMS
        </Typography>
        <Typography variant="h5" color="secondary" gutterBottom>
          Lorem Ipsum Dolor Sit
        </Typography>
        <Typography variant="body1" color="text.secondary" maxWidth={600}>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed diam nonummy nibh euismod tincidunt ut
          laoreet dolore magna aliquam erat volutpat.
        </Typography>

        {/* CTA Button - Navigates to ClientLogin */}
        <motion.div whileHover={{ scale: 1.1 }}>
          <Button
            variant="contained"
            color="primary"
            sx={{ mt: 3, px: 4, py: 1.5, fontSize: "1rem" }}
            onClick={() => navigate("/client-login")}
          >
            Get Started
          </Button>
        </motion.div>
      </motion.div>

      {/* Features Section */}
      <Grid container spacing={4} justifyContent="center" sx={{ mt: 6 }}>
        {[
          { icon: <PlayCircleOutline fontSize="large" color="primary" />, text: "Quick Start" },
          { icon: <Settings fontSize="large" color="secondary" />, text: "Easy Setup" },
          { icon: <Person fontSize="large" color="success" />, text: "User Friendly" },
          { icon: <Code fontSize="large" color="warning" />, text: "Developer Ready" }
        ].map((feature, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <motion.div initial="hidden" animate="visible" variants={cardVariants}>
              <Card sx={{ display: "flex", alignItems: "center", p: 2, boxShadow: 3, cursor: "pointer" }}>
                {feature.icon}
                <CardContent>
                  <Typography variant="h6">{feature.text}</Typography>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>

      {/* Footer */}
      <Box sx={{ mt: 6, display: "flex", gap: 2 }}>
        <Typography variant="body2" color="text.secondary">
          © 2025 CMS. All rights reserved.
        </Typography>
      </Box>
    </Box>
  );
};

export default HomePage;
