import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Button, Table, Modal, Input, Form, Typography, message, Spin, Card, Alert } from "antd";
import { EditOutlined, PlusOutlined } from "@ant-design/icons";
import "antd/dist/reset.css";

const { Title } = Typography;

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [showEditClientForm, setShowEditClientForm] = useState(false);
  const [editProject, setEditProject] = useState(null);
  const [form] = Form.useForm();
  const [clientForm] = Form.useForm();

  useEffect(() => {
    const fetchClientDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5005/api/client/${clientId}`);
        const data = await response.json();
        setClient(data);
        clientForm.setFieldsValue(data);
      } catch (err) {
        setError("Failed to load client details.");
      }
    };

    const fetchClientProjects = async () => {
      try {
        const response = await fetch(`http://localhost:5005/api/projects/${clientId}`);
        const data = await response.json();
        setProjects(data);
      } catch (err) {
        setError("Failed to load projects.");
      } finally {
        setLoading(false);
      }
    };

    fetchClientDetails();
    fetchClientProjects();
  }, [clientId]);

  const columns = [
    { title: "Project Name", dataIndex: "project_name", key: "project_name" },
    { title: "Description", dataIndex: "description", key: "description" },
    { title: "Start Date", dataIndex: "start_date", key: "start_date", render: (date) => new Date(date).toLocaleDateString() },
    { title: "End Date", dataIndex: "end_date", key: "end_date", render: (date) => new Date(date).toLocaleDateString() },
    { title: "Budget", dataIndex: "budget", key: "budget" },
    {
      title: "Actions",
      key: "actions",
      render: (_, record) => (
        <Button icon={<EditOutlined />} onClick={() => {
          setEditProject(record);
          setShowForm(true);
          form.setFieldsValue(record);
        }} />
      ),
    },
  ];

  if (loading) return <Spin size="large" style={{ display: "block", margin: "auto" }} />;
  if (error) return <Alert message={error} type="error" showIcon />;
  if (!client) return <Typography>Loading...</Typography>;

  return (
    <div style={{ padding: "24px" }}>
      <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
        <Title level={2}>Pending Users</Title>
      </div>
      <Card>
        <Title level={3}>
          {client.client_name} <EditOutlined onClick={() => setShowEditClientForm(true)} style={{ cursor: "pointer", marginLeft: 10 }} />
        </Title>
        <Table dataSource={projects} rowKey="_id" columns={columns} />
        <Button type="primary" icon={<PlusOutlined />} onClick={() => { setEditProject(null); setShowForm(true); }} style={{ marginTop: "20px" }}>
          Add New Project
        </Button>
      </Card>
    </div>
  );
};

export default ClientProfile;
