import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ClientProfile = () => {
  const { clientId } = useParams(); // Get client ID from the URL
  const [client, setClient] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch client data from the backend
    const fetchClient = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/clients/${clientId}`);
        setClient(response.data); // Set the client data
        setLoading(false);
      } catch (err) {
        console.error("Error fetching client data:", err);
        setError("Failed to load client details.");
        setLoading(false);
      }
    };
    fetchClient();
  }, [clientId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="client-profile">
      <h1>Client Profile: {client.client_name}</h1>
      <p><strong>Industry:</strong> {client.industry}</p>
      <p><strong>Status:</strong> {client.status}</p>
      <p><strong>Contact Person:</strong> {client.contact_person}</p>
      <p><strong>Location:</strong> {`${client.address.street}, ${client.address.city}, ${client.address.state}, ${client.address.postal_code}, ${client.address.country}`}</p>
      <p><strong>Description:</strong> {client.description || 'No description available'}</p>
      <p><strong>Additional Info:</strong> {client.additional_info || 'No additional info available'}</p>
      {/* You can add more fields here as needed */}
    </div>
  );
};

export default ClientProfile;
