import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Button, TextField, Select, MenuItem, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Modal, Box, Typography } from '@mui/material';
import PendingUsers from "../components/PendingUser";
import './Dashboard.css';

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState('');
  const [filters, setFilters] = useState({ region: '', industry: '', status: '' });
  const [sortOrder, setSortOrder] = useState('asc');
  const [sortColumn, setSortColumn] = useState('client_name');
  const [authToken] = useState(localStorage.getItem('token') || '');
  const [editingClient, setEditingClient] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => { fetchClients(); }, [filters, sortColumn, sortOrder]);

  const fetchClients = () => {
    const query = new URLSearchParams({
      search,
      ...filters,
      sortColumn,
      sortOrder,
    });

    axios.get(`http://localhost:5005/api/users?${query.toString()}`, {
      headers: { Authorization: `Bearer ${authToken}` },
    })
    .then((response) => setClients(response.data))
    .catch((err) => console.error('Error fetching clients:', err));
  };

  const handleSearch = (e) => setSearch(e.target.value);
  const handleFilterChange = (e) => setFilters({ ...filters, [e.target.name]: e.target.value });
  const handleSort = (column) => {
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    setSortColumn(column);
  };

  const handleEditClick = (client) => setEditingClient(client);
  const handleEditChange = (e) => setEditingClient({ ...editingClient, [e.target.name]: e.target.value });
  const handleAddressChange = (e) => setEditingClient({ ...editingClient, address: { ...editingClient.address, [e.target.name]: e.target.value } });

  const handleEditSubmit = async () => {
    try {
      await axios.put(`http://localhost:5005/api/users/${editingClient._id}`, editingClient, {
        headers: { Authorization: `Bearer ${authToken}` },
      });
      setClients(clients.map((client) => (client._id === editingClient._id ? editingClient : client)));
      setEditingClient(null);
    } catch (err) {
      console.error('Error updating client:', err);
      setError('Failed to update client. Please try again.');
    }
  };

  const handleDelete = (clientId) => {
    axios.delete(`http://localhost:5005/api/users/${clientId}`, {
      headers: { Authorization: `Bearer ${authToken}` },
    })
    .then(() => setClients(clients.filter((client) => client._id !== clientId)))
    .catch((err) => console.error('Error deleting client:', err));
  };

  return (
    <div className="dashboard p-5">
      <PendingUsers />
      <Typography variant="h4" className="mb-4">Clients</Typography>
      <TextField label="Search clients..." variant="outlined" fullWidth value={search} onChange={handleSearch} className="mb-3" />

      <div className="flex gap-4 mb-4">
        <Select name="region" value={filters.region} onChange={handleFilterChange} displayEmpty>
          <MenuItem value="">All Regions</MenuItem>
          <MenuItem value="North America">North America</MenuItem>
          <MenuItem value="Europe">Europe</MenuItem>
          <MenuItem value="Asia">Asia</MenuItem>
        </Select>
        <Select name="industry" value={filters.industry} onChange={handleFilterChange} displayEmpty>
          <MenuItem value="">All Industries</MenuItem>
          <MenuItem value="Finance">Finance</MenuItem>
          <MenuItem value="Technology">Technology</MenuItem>
          <MenuItem value="Healthcare">Healthcare</MenuItem>
        </Select>
        <Select name="status" value={filters.status} onChange={handleFilterChange} displayEmpty>
          <MenuItem value="">All Statuses</MenuItem>
          <MenuItem value="Active">Active</MenuItem>
          <MenuItem value="Inactive">Inactive</MenuItem>
        </Select>
      </div>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell onClick={() => handleSort('client_name')}>Client Name</TableCell>
              <TableCell>Status</TableCell>
              <TableCell>Industry</TableCell>
              <TableCell>Email</TableCell>
              <TableCell>Phone</TableCell>
              <TableCell>Contact Person</TableCell>
              <TableCell>Action</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {clients.map((client) => (
              <TableRow key={client._id}>
                <TableCell><Link to={`/client/${client._id}`}>{client.client_name}</Link></TableCell>
                <TableCell>{client.status}</TableCell>
                <TableCell>{client.industry}</TableCell>
                <TableCell>{client.email}</TableCell>
                <TableCell>{client.phone}</TableCell>
                <TableCell>{client.contact_person}</TableCell>
                <TableCell>
                  <Button onClick={() => handleEditClick(client)} variant="contained" color="primary">Edit</Button>
                  <Button onClick={() => handleDelete(client._id)} variant="contained" color="secondary">Delete</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Modal open={Boolean(editingClient)} onClose={() => setEditingClient(null)}>
        <Box className="p-5 bg-white w-1/3 mx-auto mt-20">
          <Typography variant="h6">Edit Client</Typography>
          <TextField label="Client Name" name="client_name" value={editingClient?.client_name} onChange={handleEditChange} fullWidth className="mb-2" />
          <TextField label="Contact Person" name="contact_person" value={editingClient?.contact_person} onChange={handleEditChange} fullWidth className="mb-2" />
          <TextField label="Email" name="email" value={editingClient?.email} onChange={handleEditChange} fullWidth className="mb-2" />
          <TextField label="Phone" name="phone" value={editingClient?.phone} onChange={handleEditChange} fullWidth className="mb-2" />
          <TextField label="Industry" name="industry" value={editingClient?.industry} onChange={handleEditChange} fullWidth className="mb-2" />
          <Button onClick={handleEditSubmit} variant="contained" color="primary">Save</Button>
          <Button onClick={() => setEditingClient(null)} variant="contained" color="secondary">Cancel</Button>
          {error && <Typography color="error">{error}</Typography>}
        </Box>
      </Modal>
    </div>
  );
};

export default Dashboard;
