import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import ProjectDetailPage from './components/ProjectDetail';
import AdminLogin from './components/AdminLogin';
import ClientLogin from './components/ClientLogin';
import Dashboard from './components/Dashboard';
import ClientProfile from './components/ClientProfile';
import HomePage from './components/HomePage';
import ContractManagement from './components/ContractManagement';
import ActivityTracking from './components/ActivityTracking';
import Header from './components/Header';
import Footer from './components/Footer';
import ProjectsPage from './components/ProjectPage';
import ClientRegister from './components/ClientRegister';

const App = () => {
  const token = localStorage.getItem('jwtToken');
  const isAuthenticated = !!token;

  const getUserRole = () => {
    if (!token) return null;
    const decodedToken = JSON.parse(atob(token.split('.')[1])); // Decode JWT
    return decodedToken.role;
  };

  const role = getUserRole();

  return (
    <Router>
      <div className="app-container">  {/* Main Flexbox Container */}
        <Header />
        <main className="content"> {/* Content area with flex-grow */}
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route
              path="/admin-login"
              element={isAuthenticated && role === 'admin' ? <Navigate to="/dashboard" /> : <AdminLogin />}
            />
            <Route
              path="/client-login"
              element={isAuthenticated && role === 'client' ? <Navigate to={`/client/${role.clientId}`} /> : <ClientLogin />}
            />
            <Route path="/client-register" element={<ClientRegister />} />

            {/* Protected Routes */}
            <Route
              path="/dashboard"
              element={isAuthenticated && role === 'admin' ? <Dashboard /> : <Navigate to="/admin-login" />}
            />
            <Route
              path="/activity-tracking"
              element={isAuthenticated && role === 'admin' ? <ActivityTracking /> : <Navigate to="/admin-login" />}
            />
            <Route
              path="/admin/projects"
              element={isAuthenticated && role === 'admin' ? <ProjectsPage /> : <Navigate to="/admin-login" />}
            />
            <Route
              path="/project/:projectId"
              element={isAuthenticated && role === 'admin' ? <ProjectDetailPage /> : <Navigate to="/admin-login" />}
            />
            <Route
              path="/client/:clientId"
              element={isAuthenticated ? <ClientProfile /> : <Navigate to="/client-login" />}
            />
            <Route path="/contract-management/:clientId" element={<ContractManagement />} />
            <Route
              path="/activity-tracking"
              element={isAuthenticated && role === 'client' ? <ActivityTracking /> : <Navigate to="/client-login" />}
            />

            {/* Fallback Route */}
            <Route path="*" element={<h2>404 - Page Not Found</h2>} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
