import React from 'react';
import { useNavigate } from 'react-router-dom';

const Header = () => {
  const navigate = useNavigate(); // Hook to programmatically navigate
  const isAuthenticated = !!localStorage.getItem('jwtToken'); // Check if the user is authenticated

  const handleLogout = () => {
    localStorage.removeItem('jwtToken'); // Clear token from localStorage
    navigate('/'); // Redirect to HomePage
  };

  return (
    <header style={headerStyle}>
      <div>
        <h1>Client Management System</h1>
      </div>
      <nav>
        {isAuthenticated ? (
          <button onClick={handleLogout} style={linkStyle}>Logout</button>
        ) : (
          <Link to="/login" style={linkStyle}>Login</Link>
        )}
      </nav>
    </header>
  );
};

const headerStyle = {
  backgroundColor: '#000',
  color: '#fff',
  padding: '10px 20px',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
};

const linkStyle = {
  color: '#fff',
  textDecoration: 'none',
  fontSize: '16px',
};

export default Header;
