import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { TextField, Button, Typography, Container, Grid, Paper, InputAdornment } from '@mui/material';
import { AccountCircle, Email, Phone, Business, LocationOn, Lock } from '@mui/icons-material';

const ClientRegister = () => {
  const [formData, setFormData] = useState({
    client_name: '',
    contact_person: '',
    email: '',
    phone: '',
    industry: '',
    status: 'Active',
    address: { street: '', city: '', state: '', postal_code: '', country: '' },
    password: '',
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, address: { ...formData.address, [name]: value } });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5005/api/register', formData);
      navigate('/client-login');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to register. Please try again.');
    }
  };

  return (
    <Container maxWidth="sm">
      <Paper elevation={3} sx={{ padding: 4, marginTop: 5, borderRadius: 2 }}>
        <Typography variant="h4" align="center" gutterBottom>Client Registration</Typography>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Client Name"
                name="client_name"
                value={formData.client_name}
                onChange={handleChange}
                required
                InputProps={{ startAdornment: (<InputAdornment position="start"><AccountCircle /></InputAdornment>) }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Contact Person"
                name="contact_person"
                value={formData.contact_person}
                onChange={handleChange}
                required
                InputProps={{ startAdornment: (<InputAdornment position="start"><AccountCircle /></InputAdornment>) }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Email"
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                InputProps={{ startAdornment: (<InputAdornment position="start"><Email /></InputAdornment>) }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Phone"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                required
                InputProps={{ startAdornment: (<InputAdornment position="start"><Phone /></InputAdornment>) }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Industry"
                name="industry"
                value={formData.industry}
                onChange={handleChange}
                required
                InputProps={{ startAdornment: (<InputAdornment position="start"><Business /></InputAdornment>) }}
              />
            </Grid>
            <Grid item xs={12}>
              <Typography variant="h6">Address</Typography>
            </Grid>
            {['street', 'city', 'state', 'postal_code', 'country'].map((field, index) => (
              <Grid item xs={12} key={index}>
                <TextField
                  fullWidth
                  label={field.replace('_', ' ').toUpperCase()}
                  name={field}
                  value={formData.address[field]}
                  onChange={handleAddressChange}
                  required
                  InputProps={{ startAdornment: (<InputAdornment position="start"><LocationOn /></InputAdornment>) }}
                />
              </Grid>
            ))}
            <Grid item xs={12}>
              <TextField
                fullWidth
                type="password"
                label="Password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
                InputProps={{ startAdornment: (<InputAdornment position="start"><Lock /></InputAdornment>) }}
              />
            </Grid>
            <Grid item xs={12}>
              <Button fullWidth variant="contained" color="primary" type="submit">Register</Button>
            </Grid>
            {error && (
              <Grid item xs={12}>
                <Typography color="error" align="center">{error}</Typography>
              </Grid>
            )}
          </Grid>
        </form>
      </Paper>
    </Container>
  );
};

export default ClientRegister;
