import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Card,
  CardContent,
  Typography,
  TextField,
  Select,
  MenuItem,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  InputLabel,
  FormControl,
} from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";
import { Link } from "react-router-dom";
import PendingUsers from "../components/PendingUser";

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState("");
  const [filters, setFilters] = useState({ region: "", industry: "", status: "" });
  const [sortOrder, setSortOrder] = useState("asc");
  const [sortColumn, setSortColumn] = useState("client_name");
  const [authToken] = useState(localStorage.getItem("token") || "");
  const [editingClient, setEditingClient] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchClients();
  }, [filters, sortColumn, sortOrder]);

  const fetchClients = () => {
    const query = new URLSearchParams({
      search,
      region: filters.region,
      industry: filters.industry,
      status: filters.status,
      sortColumn,
      sortOrder,
    });

    axios
      .get(`http://localhost:5005/api/users?${query.toString()}`, {
        headers: { Authorization: `Bearer ${authToken}` },
      })
      .then((response) => setClients(response.data))
      .catch((err) => console.error("Error fetching clients:", err));
  };

  const handleSearch = (e) => setSearch(e.target.value);
  const handleFilterChange = (e) => setFilters({ ...filters, [e.target.name]: e.target.value });
  const handleSort = (column) => setSortOrder(sortOrder === "asc" ? "desc" : "asc");
  const handleEditClick = (client) => setEditingClient(client);
  const handleEditChange = (e) => setEditingClient({ ...editingClient, [e.target.name]: e.target.value });

  const handleEditSubmit = async () => {
    try {
      await axios.put(`http://localhost:5005/api/users/${editingClient._id}`, editingClient, {
        headers: { Authorization: `Bearer ${authToken}` },
      });
      setClients(clients.map((c) => (c._id === editingClient._id ? editingClient : c)));
      setEditingClient(null);
    } catch (err) {
      console.error("Error updating client:", err);
      setError("Failed to update client. Please try again.");
    }
  };

  const handleDelete = (clientId) => {
    axios
      .delete(`http://localhost:5005/api/users/${clientId}`, { headers: { Authorization: `Bearer ${authToken}` } })
      .then(() => setClients(clients.filter((c) => c._id !== clientId)))
      .catch((err) => console.error("Error deleting client:", err));
  };

  const columns = [
    { field: "client_name", headerName: "Client Name", flex: 1, renderCell: (params) => <Link to={`/client/${params.row._id}`}>{params.value}</Link> },
    { field: "status", headerName: "Status", flex: 1 },
    { field: "industry", headerName: "Industry", flex: 1 },
    { field: "email", headerName: "Email", flex: 1 },
    { field: "phone", headerName: "Phone", flex: 1 },
    { field: "contact_person", headerName: "Contact Person", flex: 1 },
    {
      field: "actions",
      headerName: "Actions",
      flex: 1,
      renderCell: (params) => (
        <>
          <Button variant="contained" color="primary" size="small" onClick={() => handleEditClick(params.row)} style={{ marginRight: 8 }}>
            Edit
          </Button>
          <Button variant="contained" color="error" size="small" onClick={() => handleDelete(params.row._id)}>
            Delete
          </Button>
        </>
      ),
    },
  ];

  return (
    <Card sx={{ maxWidth: "90%", margin: "auto", mt: 4, p: 3 }}>
      <CardContent>
        <PendingUsers />
        <Typography variant="h4" gutterBottom>
          Clients Dashboard
        </Typography>

        {/* Filters & Search */}
        <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
          <TextField label="Search Clients..." variant="outlined" value={search} onChange={handleSearch} fullWidth />
          <FormControl fullWidth>
            <InputLabel>Region</InputLabel>
            <Select name="region" value={filters.region} onChange={handleFilterChange}>
              <MenuItem value="">All Regions</MenuItem>
              <MenuItem value="North America">North America</MenuItem>
              <MenuItem value="Europe">Europe</MenuItem>
              <MenuItem value="Asia">Asia</MenuItem>
            </Select>
          </FormControl>
          <FormControl fullWidth>
            <InputLabel>Industry</InputLabel>
            <Select name="industry" value={filters.industry} onChange={handleFilterChange}>
              <MenuItem value="">All Industries</MenuItem>
              <MenuItem value="Finance">Finance</MenuItem>
              <MenuItem value="Technology">Technology</MenuItem>
              <MenuItem value="Healthcare">Healthcare</MenuItem>
            </Select>
          </FormControl>
          <FormControl fullWidth>
            <InputLabel>Status</InputLabel>
            <Select name="status" value={filters.status} onChange={handleFilterChange}>
              <MenuItem value="">All Statuses</MenuItem>
              <MenuItem value="Active">Active</MenuItem>
              <MenuItem value="Inactive">Inactive</MenuItem>
            </Select>
          </FormControl>
        </div>

        {/* Clients Table */}
        <DataGrid
          rows={clients}
          columns={columns}
          getRowId={(row) => row._id}
          pageSize={10}
          autoHeight
          sx={{ background: "#fff", borderRadius: "8px" }}
        />

        {/* Edit Client Modal */}
        <Dialog open={!!editingClient} onClose={() => setEditingClient(null)} maxWidth="sm" fullWidth>
          <DialogTitle>Edit Client</DialogTitle>
          <DialogContent>
            <TextField fullWidth label="Client Name" name="client_name" value={editingClient?.client_name || ""} onChange={handleEditChange} margin="dense" />
            <TextField fullWidth label="Contact Person" name="contact_person" value={editingClient?.contact_person || ""} onChange={handleEditChange} margin="dense" />
            <TextField fullWidth label="Email" name="email" value={editingClient?.email || ""} onChange={handleEditChange} margin="dense" />
            <TextField fullWidth label="Phone" name="phone" value={editingClient?.phone || ""} onChange={handleEditChange} margin="dense" />
            <TextField fullWidth label="Industry" name="industry" value={editingClient?.industry || ""} onChange={handleEditChange} margin="dense" />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleEditSubmit} variant="contained" color="primary">
              Save
            </Button>
            <Button onClick={() => setEditingClient(null)} variant="contained" color="secondary">
              Cancel
            </Button>
          </DialogActions>
        </Dialog>
      </CardContent>
    </Card>
  );
};

export default Dashboard;
