import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import ClientProfile from './components/ClientProfile';
import HomePage from './components/HomePage';
import ContractManagement from './components/ContractManagement'; // Add this page
import ActivityTracking from './components/ActivityTracking'; // Add this page
import Header from './components/Header';
import Footer from './components/Footer';

const App = () => {
  const isAuthenticated = !!localStorage.getItem('jwtToken'); // Check if the user is authenticated

  return (
    <Router>
      <Header />
      <Routes>
        <Route path="/" element={<HomePage />} />

        <Route 
          path="/login" 
          element={isAuthenticated ? <Navigate to="/dashboard" /> : <Login />} 
        />

        <Route 
          path="/dashboard" 
          element={isAuthenticated ? <Dashboard /> : <Navigate to="/" />} 
        />

        <Route 
          path="/client/:clientId" 
          element={isAuthenticated ? <ClientProfile /> : <Navigate to="/" />} 
        />

        {/* Client Pages */}
        <Route 
          path="/contract-management" 
          element={isAuthenticated ? <ContractManagement /> : <Navigate to="/" />} 
        />
        <Route 
          path="/activity-tracking" 
          element={isAuthenticated ? <ActivityTracking /> : <Navigate to="/" />} 
        />

        <Route path="*" element={<h2>404 - Page Not Found</h2>} />
      </Routes>
      <Footer />
    </Router>
  );
};

export default App;
