import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { 
  Container, Typography, Table, TableBody, TableCell, TableContainer, 
  TableHead, TableRow, Paper, Button, TextField, Dialog, DialogActions, 
  DialogContent, DialogTitle, CircularProgress, Alert 
} from "@mui/material";

const ClientProfile = () => {
  const { clientId } = useParams();
  const [client, setClient] = useState(null);
  const [projects, setProjects] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [openForm, setOpenForm] = useState(false);
  const [formData, setFormData] = useState({
    project_name: "",
    description: "",
    start_date: "",
    end_date: "",
    budget: "",
    client_id: clientId,
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [clientRes, projectsRes] = await Promise.all([
          axios.get(`http://localhost:5005/api/client/${clientId}`),
          axios.get(`http://localhost:5005/api/projects/${clientId}`),
        ]);
        setClient(clientRes.data);
        setProjects(
          projectsRes.data.map(project => ({
            ...project,
            team_members: Array.isArray(project.team_members) ? project.team_members : [],
          }))
        );
      } catch (err) {
        setError("Failed to load data.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [clientId]);

  const handleFormChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5005/api/projects", formData);
      setProjects([...projects, response.data]);
      setOpenForm(false);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  if (loading) return <CircularProgress sx={{ display: "block", margin: "20px auto" }} />;
  if (error) return <Alert severity="error">{error}</Alert>;
  if (!client) return <Alert severity="warning">Client not found.</Alert>;

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" align="center" gutterBottom>
        {client.client_name}'s Projects
      </Typography>

      <TableContainer component={Paper} sx={{ mt: 2 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Project Name</TableCell>
              <TableCell>Description</TableCell>
              <TableCell>Start Date</TableCell>
              <TableCell>End Date</TableCell>
              <TableCell>Budget</TableCell>
              <TableCell>Team Members</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {projects.length === 0 ? (
              <TableRow>
                <TableCell colSpan={6} align="center">No projects found.</TableCell>
              </TableRow>
            ) : (
              projects.map((project) => (
                <TableRow key={project._id}>
                  <TableCell>{project.project_name}</TableCell>
                  <TableCell>{project.description}</TableCell>
                  <TableCell>{new Date(project.start_date).toLocaleDateString()}</TableCell>
                  <TableCell>{new Date(project.end_date).toLocaleDateString()}</TableCell>
                  <TableCell>${project.budget}</TableCell>
                  <TableCell>{project.team_members.join(", ")}</TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>

      <Button variant="contained" color="primary" sx={{ mt: 3 }} onClick={() => setOpenForm(true)}>
        Add New Project
      </Button>

      <Dialog open={openForm} onClose={() => setOpenForm(false)}>
        <DialogTitle>Create a New Project</DialogTitle>
        <DialogContent>
          <TextField fullWidth margin="dense" label="Project Name" name="project_name" value={formData.project_name} onChange={handleFormChange} required />
          <TextField fullWidth margin="dense" label="Description" name="description" value={formData.description} onChange={handleFormChange} multiline rows={3} />
          <TextField fullWidth margin="dense" label="Start Date" name="start_date" type="date" value={formData.start_date} onChange={handleFormChange} InputLabelProps={{ shrink: true }} />
          <TextField fullWidth margin="dense" label="End Date" name="end_date" type="date" value={formData.end_date} onChange={handleFormChange} InputLabelProps={{ shrink: true }} />
          <TextField fullWidth margin="dense" label="Budget" name="budget" type="number" value={formData.budget} onChange={handleFormChange} />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenForm(false)} color="secondary">Cancel</Button>
          <Button onClick={handleFormSubmit} color="primary" variant="contained">Create</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default ClientProfile;
